INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (0, 'missingno', 'flying', 'none', 0, 0, 'MissingNO is a programming quirk&#44; and not a real part of the game. When you get this&#44; your game can perform strangely&#44; and the graphics will often become scrambled. The MissingNO Pok&#233;mon is most often found after you perform the Fight Safari Zone Pok&#233;mon trick.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (1, 'bulbasaur', 'grass', 'poison', 0, 1, 'Bulbasaur can be seen napping in bright sunlight. There is a seed on its back. By soaking up the sun’s rays&#44; the seed grows progressively larger.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (2, 'ivysaur', 'grass', 'poison', 1, 1, 'There is a bud on this Pok&#233;mon’s back. To support its weight&#44; Ivysaur’s legs and trunk grow thick and strong. If it starts spending more time lying in the sunlight&#44; it’s a sign that the bud will bloom into a large flower soon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (3, 'venusaur', 'grass', 'poison', 2, 1, 'There is a large flower on Venusaur’s back. The flower is said to take on vivid colors if it gets plenty of nutrition and sunlight. The flower’s aroma soothes the emotions of people.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (4, 'charmander', 'fire', 'none', 0, 2, 'The flame that burns at the tip of its tail is an indication of its emotions. The flame wavers when Charmander is enjoying itself. If the Pok&#233;mon becomes enraged&#44; the flame burns fiercely.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (5, 'charmeleon', 'fire', 'none', 4, 2, 'Charmeleon mercilessly destroys its foes using its sharp claws. If it encounters a strong foe&#44; it turns aggressive. In this excited state&#44; the flame at the tip of its tail flares with a bluish white color.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (6, 'charizard', 'fire', 'flying', 5, 2, 'Charizard flies around the sky in search of powerful opponents. It breathes fire of such great heat that it melts anything. However&#44; it never turns its fiery breath on any opponent weaker than itself.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (7, 'squirtle', 'water', 'none', 0, 3, 'Squirtle’s shell is not merely used for protection. The shell’s rounded shape and the grooves on its surface help minimize resistance in water&#44; enabling this Pok&#233;mon to swim at high speeds.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (8, 'wartortle', 'water', 'none', 7, 3, 'Its tail is large and covered with a rich&#44; thick fur. The tail becomes increasingly deeper in color as Wartortle ages. The scratches on its shell are evidence of this Pok&#233;mon’s toughness as a battler.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (9, 'blastoise', 'water', 'none', 8, 3, 'Blastoise has water spouts that protrude from its shell. The water spouts are very accurate. They can shoot bullets of water with enough accuracy to strike empty cans from a distance of over 160 feet.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (10, 'caterpie', 'bug', 'none', 0, 4, 'Caterpie has a voracious appetite. It can devour leaves bigger than its body right before your eyes. From its antenna&#44; this Pok&#233;mon releases a terrifically strong odor.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (11, 'metapod', 'bug', 'none', 10, 4, 'The shell covering this Pok&#233;mon’s body is as hard as an iron slab. Metapod does not move very much. It stays still because it is preparing its soft innards for evolution inside the hard shell.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (12, 'butterfree', 'bug', 'flying', 11, 4, 'Butterfree has a superior ability to search for delicious honey from flowers. It can even search out&#44; extract&#44; and carry honey from flowers that are blooming over six miles from its nest.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (13, 'weedle', 'bug', 'poison', 0, 5, 'Weedle has an extremely acute sense of smell. It is capable of distinguishing its favorite kinds of leaves from those it dislikes just by sniffing with its big red proboscis (nose).');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (14, 'kakuna', 'bug', 'poison', 13, 5, 'Kakuna remains virtually immobile as it clings to a tree. However&#44; on the inside&#44; it is extremely busy as it prepares for its coming evolution. This is evident from how hot the shell becomes to the touch.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (15, 'beedrill', 'bug', 'poison', 14, 5, 'Beedrill is extremely territorial. No one should ever approach its nest—this is for their own safety. If angered&#44; they will attack in a furious swarm.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (16, 'pidgey', 'normal', 'flying', 0, 6, 'Pidgey has an extremely sharp sense of direction. It is capable of unerringly returning home to its nest&#44; however far it may be removed from its familiar surroundings.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (17, 'pidgeotto', 'normal', 'flying', 16, 6, 'Pidgeotto claims a large area as its own territory. This Pok&#233;mon flies around&#44; patrolling its living space. If its territory is violated&#44; it shows no mercy in thoroughly punishing the foe with its sharp claws.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (18, 'pidgeot', 'normal', 'flying', 17, 6, 'This Pok&#233;mon has a dazzling plumage of beautifully glossy feathers. Many Trainers are captivated by the striking beauty of the feathers on its head&#44; compelling them to choose Pidgeot as their Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (19, 'rattata', 'normal', 'none', 0, 7, 'Rattata is cautious in the extreme. Even while it is asleep&#44; it constantly listens by moving its ears around. It is not picky about where it lives—it will make its nest anywhere.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (20, 'raticate', 'normal', 'none', 19, 7, 'Raticate’s sturdy fangs grow steadily. To keep them ground down&#44; it gnaws on rocks and logs. It may even chew on the walls of houses.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (21, 'spearow', 'normal', 'flying', 0, 8, 'Spearow has a very loud cry that can be heard over half a mile away. If its high&#44; keening cry is heard echoing all around&#44; it is a sign that they are warning each other of danger.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (22, 'fearow', 'normal', 'flying', 21, 8, 'Fearow is recognized by its long neck and elongated beak. They are conveniently shaped for catching prey in soil or water. It deftly moves its long and skinny beak to pluck prey.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (23, 'ekans', 'poison', 'none', 0, 9, 'Ekans curls itself up in a spiral while it rests. Assuming this position allows it to quickly respond to a threat from any direction with a glare from its upraised head.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (24, 'arbok', 'poison', 'none', 23, 9, 'This Pok&#233;mon is terrifically strong in order to constrict things with its body. It can even flatten steel oil drums. Once Arbok wraps its body around its foe&#44; escaping its crunching embrace is impossible.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (25, 'pikachu', 'electric', 'none', 172, 10, 'This Pok&#233;mon has electricity-storing pouches on its cheeks. These appear to become electrically charged during the night while Pikachu sleeps. It occasionally discharges electricity when it is dozy after waking up.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (26, 'raichu', 'electric', 'none', 25, 10, 'This Pok&#233;mon exudes a weak electrical charge from all over its body that makes it take on a slight glow in darkness. Raichu plants its tail in the ground to discharge electricity.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (27, 'sandshrew', 'ground', 'none', 0, 11, 'Sandshrew has a very dry hide that is extremely tough. The Pok&#233;mon can roll into a ball that repels any attack. At night&#44; it burrows into the desert sand to sleep.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (28, 'sandslash', 'ground', 'none', 27, 11, 'Sandslash can roll up its body as if it were a ball covered with large spikes. In battle&#44; this Pok&#233;mon will try to make the foe flinch by jabbing it with its spines. It then leaps at the stunned foe to tear wildly with its sharp claws.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (29, 'nidoran-f', 'poison', 'none', 0, 12, 'Nidoran♀ has barbs that secrete a powerful poison. They are thought to have developed as protection for this small-bodied Pok&#233;mon. When enraged&#44; it releases a horrible toxin from its horn.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (30, 'nidorina', 'poison', 'none', 29, 12, 'When Nidorina are with their friends or family&#44; they keep their barbs tucked away to prevent hurting each other. This Pok&#233;mon appears to become nervous if separated from the others.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (31, 'nidoqueen', 'poison', 'ground', 30, 12, 'Nidoqueen’s body is encased in extremely hard scales. It is adept at sending foes flying with harsh tackles. This Pok&#233;mon is at its strongest when it is defending its young.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (32, 'nidoran-m', 'poison', 'none', 0, 13, 'Nidoran♂ has developed muscles for moving its ears. Thanks to them&#44; the ears can be freely moved in any direction. Even the slightest sound does not escape this Pok&#233;mon’s notice.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (33, 'nidorino', 'poison', 'none', 32, 13, 'Nidorino has a horn that is harder than a diamond. If it senses a hostile presence&#44; all the barbs on its back bristle up at once&#44; and it challenges the foe with all its might.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (34, 'nidoking', 'poison', 'ground', 33, 13, 'Nidoking’s thick tail packs enormously destructive power. With one swing&#44; it can topple a metal transmission tower. Once this Pok&#233;mon goes on a rampage&#44; there is no stopping it.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (35, 'clefairy', 'fairy', 'none', 173, 14, 'On every night of a full moon&#44; groups of this Pok&#233;mon come out to play. When dawn arrives&#44; the tired Clefairy return to their quiet mountain retreats and go to sleep nestled up against each other.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (36, 'clefable', 'fairy', 'none', 35, 14, 'Clefable moves by skipping lightly as if it were flying using its wings. Its bouncy step lets it even walk on water. It is known to take strolls on lakes on quiet&#44; moonlit nights.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (37, 'vulpix', 'fire', 'none', 0, 15, 'Inside Vulpix’s body burns a flame that never goes out. During the daytime&#44; when the temperatures rise&#44; this Pok&#233;mon releases flames from its mouth to prevent its body from growing too hot.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (38, 'ninetales', 'fire', 'none', 37, 15, 'Legend has it that Ninetales came into being when nine wizards possessing sacred powers merged into one. This Pok&#233;mon is highly intelligent—it can understand human speech.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (39, 'jigglypuff', 'normal', 'fairy', 174, 16, 'When this Pok&#233;mon sings&#44; it never pauses to breathe. If it is in a battle against an opponent that does not easily fall asleep&#44; Jigglypuff cannot breathe&#44; endangering its life.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (40, 'wigglytuff', 'normal', 'fairy', 39, 16, 'Wigglytuff’s body is very flexible. By inhaling deeply&#44; this Pok&#233;mon can inflate itself seemingly without end. Once inflated&#44; Wigglytuff bounces along lightly like a balloon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (41, 'zubat', 'poison', 'flying', 0, 17, 'Zubat avoids sunlight because exposure causes it to become unhealthy. During the daytime&#44; it stays in caves or under the eaves of old houses&#44; sleeping while hanging upside down.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (42, 'golbat', 'poison', 'flying', 41, 17, 'Golbat bites down on prey with its four fangs and drinks the victim’s blood. It becomes active on inky dark moonless nights&#44; flying around to attack people and Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (43, 'oddish', 'grass', 'poison', 0, 18, 'Oddish searches for fertile&#44; nutrient-rich soil&#44; then plants itself. During the daytime&#44; while it is planted&#44; this Pok&#233;mon’s feet are thought to change shape and become similar to the roots of trees.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (44, 'gloom', 'grass', 'poison', 43, 18, 'From its mouth Gloom drips honey that smells absolutely horrible. Apparently&#44; it loves the horrid stench. It sniffs the noxious fumes and then drools even more of its honey.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (45, 'vileplume', 'grass', 'poison', 44, 18, 'Vileplume has the world’s largest petals. They are used to attract prey that are then doused with toxic spores. Once the prey are immobilized&#44; this Pok&#233;mon catches and devours them.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (46, 'paras', 'bug', 'grass', 0, 19, 'Paras has parasitic mushrooms growing on its back called tochukaso. They grow large by drawing nutrients from this Bug Pok&#233;mon host. They are highly valued as a medicine for extending life.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (47, 'parasect', 'bug', 'grass', 46, 19, 'Parasect is known to infest large trees en masse and drain nutrients from the lower trunk and roots. When an infested tree dies&#44; they move onto another tree all at once.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (48, 'venonat', 'bug', 'poison', 0, 20, 'Venonat is said to have evolved with a coat of thin&#44; stiff hair that covers its entire body for protection. It possesses large eyes that never fail to spot even minuscule prey.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (49, 'venomoth', 'bug', 'poison', 48, 20, 'Venomoth is nocturnal—it is a Pok&#233;mon that only becomes active at night. Its favorite prey are small insects that gather around streetlights&#44; attracted by the light in the darkness.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (50, 'diglett', 'ground', 'none', 0, 21, 'Diglett are raised in most farms. The reason is simple— wherever this Pok&#233;mon burrows&#44; the soil is left perfectly tilled for planting crops. This soil is made ideal for growing delicious vegetables.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (51, 'dugtrio', 'ground', 'none', 50, 21, 'Dugtrio are actually triplets that emerged from one body. As a result&#44; each triplet thinks exactly like the other two triplets. They work cooperatively to burrow endlessly.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (52, 'meowth', 'normal', 'none', 0, 22, 'Meowth withdraws its sharp claws into its paws to slinkily sneak about without making any incriminating footsteps. For some reason&#44; this Pok&#233;mon loves shiny coins that glitter with light.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (53, 'persian', 'normal', 'none', 52, 22, 'Persian has six bold whiskers that give it a look of toughness. The whiskers sense air movements to determine what is in the Pok&#233;mon’s surrounding vicinity. It becomes docile if grabbed by the whiskers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (54, 'psyduck', 'water', 'none', 0, 23, 'If it uses its mysterious power&#44; Psyduck can’t remember having done so. It apparently can’t form a memory of such an event because it goes into an altered state that is much like deep sleep.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (55, 'golduck', 'water', 'none', 54, 23, 'Golduck is the fastest swimmer among all Pok&#233;mon. It swims effortlessly&#44; even in a rough&#44; stormy sea. It sometimes rescues people from wrecked ships floundering in high seas.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (56, 'mankey', 'fighting', 'none', 0, 24, 'When Mankey starts shaking and its nasal breathing turns rough&#44; it’s a sure sign that it is becoming angry. However&#44; because it goes into a towering rage almost instantly&#44; it is impossible for anyone to flee its wrath.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (57, 'primeape', 'fighting', 'none', 56, 24, 'When Primeape becomes furious&#44; its blood circulation is boosted. In turn&#44; its muscles are made even stronger. However&#44; it also becomes much less intelligent at the same time.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (58, 'growlithe', 'fire', 'none', 0, 25, 'Growlithe has a superb sense of smell. Once it smells anything&#44; this Pok&#233;mon won’t forget the scent&#44; no matter what. It uses its advanced olfactory sense to determine the emotions of other living things.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (59, 'arcanine', 'fire', 'none', 58, 25, 'Arcanine is known for its high speed. It is said to be capable of running over 6&#44;200 miles in a single day and night. The fire that blazes wildly within this Pok&#233;mon’s body is its source of power.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (60, 'poliwag', 'water', 'none', 0, 26, 'Poliwag has a very thin skin. It is possible to see the Pok&#233;mon’s spiral innards right through the skin. Despite its thinness&#44; however&#44; the skin is also very flexible. Even sharp fangs bounce right off it.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (61, 'poliwhirl', 'water', 'none', 60, 26, 'The surface of Poliwhirl’s body is always wet and slick with a slimy fluid. Because of this slippery covering&#44; it can easily slip and slide out of the clutches of any enemy in battle.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (62, 'poliwrath', 'water', 'fighting', 61, 26, 'Poliwrath’s highly developed&#44; brawny muscles never grow fatigued&#44; however much it exercises. It is so tirelessly strong&#44; this Pok&#233;mon can swim back and forth across the ocean without effort.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (63, 'abra', 'psychic', 'none', 0, 27, 'Abra needs to sleep for eighteen hours a day. If it doesn’t&#44; this Pok&#233;mon loses its ability to use telekinetic powers. If it is attacked&#44; Abra escapes using Teleport while it is still sleeping.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (64, 'kadabra', 'psychic', 'none', 63, 27, 'Kadabra holds a silver spoon in its hand. The spoon is used to amplify the alpha waves in its brain. Without the spoon&#44; the Pok&#233;mon is said to be limited to half the usual amount of its telekinetic powers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (65, 'alakazam', 'psychic', 'none', 64, 27, 'Alakazam’s brain continually grows&#44; infinitely multiplying brain cells. This amazing brain gives this Pok&#233;mon an astoundingly high IQ of 5&#44;000. It has a thorough memory of everything that has occurred in the world.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (66, 'machop', 'fighting', 'none', 0, 28, 'Machop exercises by hefting around a Graveler as if it were a barbell. There are some Machop that travel the world in a quest to master all kinds of martial arts.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (67, 'machoke', 'fighting', 'none', 66, 28, 'Machoke undertakes bodybuilding every day even as it helps people with tough&#44; physically demanding labor. On its days off&#44; this Pok&#233;mon heads to the fields and mountains to exercise and train.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (68, 'machamp', 'fighting', 'none', 67, 28, 'Machamp is known as the Pok&#233;mon that has mastered every kind of martial arts. If it grabs hold of the foe with its four arms&#44; the battle is all but over. The hapless foe is thrown far over the horizon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (69, 'bellsprout', 'grass', 'poison', 0, 29, 'Bellsprout’s thin and flexible body lets it bend and sway to avoid any attack&#44; however strong it may be. From its mouth&#44; this Pok&#233;mon spits a corrosive fluid that melts even iron.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (70, 'weepinbell', 'grass', 'poison', 69, 29, 'Weepinbell has a large hook on its rear end. At night&#44; the Pok&#233;mon hooks on to a tree branch and goes to sleep. If it moves around in its sleep&#44; it may wake up to find itself on the ground.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (71, 'victreebel', 'grass', 'poison', 70, 29, 'Victreebel has a long vine that extends from its head. This vine is waved and flicked about as if it were an animal to attract prey. When an unsuspecting prey draws near&#44; this Pok&#233;mon swallows it whole.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (72, 'tentacool', 'water', 'poison', 0, 30, 'Tentacool absorbs sunlight and refracts it using water inside its body to convert it into beam energy. This Pok&#233;mon shoots beams from the small round organ above its eyes.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (73, 'tentacruel', 'water', 'poison', 72, 30, 'Tentacruel has tentacles that can be freely elongated and shortened at will. It ensnares prey with its tentacles and weakens the prey by dosing it with a harsh toxin. It can catch up to 80 prey at the same time.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (74, 'geodude', 'rock', 'ground', 0, 31, 'When Geodude sleeps deeply&#44; it buries itself halfway into the ground. It will not awaken even if hikers step on it unwittingly. In the morning&#44; this Pok&#233;mon rolls downhill in search of food.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (75, 'graveler', 'rock', 'ground', 74, 31, 'Rocks are Graveler’s favorite food. This Pok&#233;mon will climb a mountain from the base to the summit&#44; crunchingly feasting on rocks all the while. Upon reaching the peak&#44; it rolls back down to the bottom.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (76, 'golem', 'rock', 'ground', 75, 31, 'Golem is known for rolling down from mountains. To prevent them from rolling into the homes of people downhill&#44; grooves have been dug into the sides of mountains to serve as guideways for diverting this Pok&#233;mon’s course.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (77, 'ponyta', 'fire', 'none', 0, 32, 'Ponyta is very weak at birth. It can barely stand up. This Pok&#233;mon becomes stronger by stumbling and falling to keep up with its parent.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (78, 'rapidash', 'fire', 'none', 77, 32, 'Rapidash usually can be seen casually cantering in the fields and plains. However&#44; when this Pok&#233;mon turns serious&#44; its fiery manes flare and blaze as it gallops its way up to 150 mph.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (79, 'slowpoke', 'water', 'psychic', 0, 33, 'Slowpoke uses its tail to catch prey by dipping it in water at the side of a river. However&#44; this Pok&#233;mon often forgets what it’s doing and often spends entire days just loafing at water’s edge.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (80, 'slowbro', 'water', 'psychic', 79, 33, 'Slowbro’s tail has a Shellder firmly attached with a bite. As a result&#44; the tail can’t be used for fishing anymore. This causes Slowbro to grudgingly swim and catch prey instead.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (81, 'magnemite', 'electric', 'steel', 0, 34, 'Magnemite floats in the air by emitting electromagnetic waves from the units at its sides. These waves block gravity. This Pok&#233;mon becomes incapable of flight if its internal electrical supply is depleted.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (82, 'magneton', 'electric', 'steel', 81, 34, 'Magneton emits a powerful magnetic force that is fatal to electronics and precision instruments. Because of this&#44; it is said that some towns warn people to keep this Pok&#233;mon inside a Pok&#233; Ball.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (83, 'farfetchd', 'normal', 'flying', 0, 35, 'Farfetch’d is always seen with a stalk from a plant of some sort. Apparently&#44; there are good stalks and bad stalks. This Pok&#233;mon has been known to fight with others over stalks.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (84, 'doduo', 'normal', 'flying', 0, 36, 'Doduo’s two heads contain completely identical brains. A scientific study reported that on rare occasions&#44; there will be examples of this Pok&#233;mon possessing different sets of brains.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (85, 'dodrio', 'normal', 'flying', 84, 36, 'Apparently&#44; the heads aren’t the only parts of the body that Dodrio has three of. It has three sets of hearts and lungs as well&#44; so it is capable of running long distances without rest.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (86, 'seel', 'water', 'none', 0, 37, 'Seel hunts for prey in the frigid sea underneath sheets of ice. When it needs to breathe&#44; it punches a hole through the ice with the sharply protruding section of its head.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (87, 'dewgong', 'water', 'ice', 86, 37, 'Dewgong loves to snooze on bitterly cold ice. The sight of this Pok&#233;mon sleeping on a glacier was mistakenly thought to be a mermaid by a mariner long ago.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (88, 'grimer', 'poison', 'none', 0, 38, 'Grimer emerged from the sludge that settled on a polluted seabed. This Pok&#233;mon loves anything filthy. It constantly leaks a horribly germ-infested fluid from all over its body.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (89, 'muk', 'poison', 'none', 88, 38, 'This Pok&#233;mon’s favorite food is anything that is repugnantly filthy. In dirty towns where people think nothing of throwing away litter on the streets&#44; Muk are certain to gather.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (90, 'shellder', 'water', 'none', 0, 39, 'At night&#44; this Pok&#233;mon uses its broad tongue to burrow a hole in the seafloor sand and then sleep in it. While it is sleeping&#44; Shellder closes its shell&#44; but leaves its tongue hanging out.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (91, 'cloyster', 'water', 'ice', 90, 39, 'Cloyster is capable of swimming in the sea. It does so by swallowing water&#44; then jetting it out toward the rear. This Pok&#233;mon shoots spikes from its shell using the same system.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (92, 'gastly', 'ghost', 'poison', 0, 40, 'Gastly is largely composed of gaseous matter. When exposed to a strong wind&#44; the gaseous body quickly dwindles away. Groups of this Pok&#233;mon cluster under the eaves of houses to escape the ravages of wind.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (93, 'haunter', 'ghost', 'poison', 92, 40, 'Haunter is a dangerous Pok&#233;mon. If one beckons you while floating in darkness&#44; you must never approach it. This Pok&#233;mon will try to lick you with its tongue and steal your life away.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (94, 'gengar', 'ghost', 'poison', 93, 40, 'Sometimes&#44; on a dark night&#44; your shadow thrown by a streetlight will suddenly and startlingly overtake you. It is actually a Gengar running past you&#44; pretending to be your shadow.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (95, 'onix', 'rock', 'ground', 0, 41, 'Onix has a magnet in its brain. It acts as a compass so that this Pok&#233;mon does not lose direction while it is tunneling. As it grows older&#44; its body becomes increasingly rounder and smoother.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (96, 'drowzee', 'psychic', 'none', 0, 42, 'If your nose becomes itchy while you are sleeping&#44; it’s a sure sign that one of these Pok&#233;mon is standing above your pillow and trying to eat your dream through your nostrils.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (97, 'hypno', 'psychic', 'none', 96, 42, 'Hypno holds a pendulum in its hand. The arcing movement and glitter of the pendulum lull the foe into a deep state of hypnosis. While this Pok&#233;mon searches for prey&#44; it polishes the pendulum.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (98, 'krabby', 'water', 'none', 0, 43, 'Krabby live on beaches&#44; burrowed inside holes dug into the sand. On sandy beaches with little in the way of food&#44; these Pok&#233;mon can be seen squabbling with each other over territory.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (99, 'kingler', 'water', 'none', 98, 43, 'Kingler has an enormous&#44; oversized claw. It waves this huge claw in the air to communicate with others. However&#44; because the claw is so heavy&#44; the Pok&#233;mon quickly tires.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (100, 'voltorb', 'electric', 'none', 0, 44, 'Voltorb is extremely sensitive—it explodes at the slightest of shocks. It is rumored that it was first created when a Pok&#233; Ball was exposed to a powerful pulse of energy.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (101, 'electrode', 'electric', 'none', 100, 44, 'One of Electrode’s characteristics is its attraction to electricity. It is a problematical Pok&#233;mon that congregates mostly at electrical power plants to feed on electricity that has just been generated.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (102, 'exeggcute', 'grass', 'psychic', 0, 45, 'This Pok&#233;mon consists of six eggs that form a closely knit cluster. The six eggs attract each other and spin around. When cracks increasingly appear on the eggs&#44; Exeggcute is close to evolution.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (103, 'exeggutor', 'grass', 'psychic', 102, 45, 'Exeggutor originally came from the tropics. Its heads steadily grow larger from exposure to strong sunlight. It is said that when the heads fall off&#44; they group together to form Exeggcute.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (104, 'cubone', 'ground', 'none', 0, 46, 'Cubone pines for the mother it will never see again. Seeing a likeness of its mother in the full moon&#44; it cries. The stains on the skull the Pok&#233;mon wears are made by the tears it sheds.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (105, 'marowak', 'ground', 'none', 104, 46, 'Marowak is the evolved form of a Cubone that has overcome its sadness at the loss of its mother and grown tough. This Pok&#233;mon’s tempered and hardened spirit is not easily broken.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (106, 'hitmonlee', 'fighting', 'none', 236, 47, 'Hitmonlee’s legs freely contract and stretch. Using these springlike legs&#44; it bowls over foes with devastating kicks. After battle&#44; it rubs down its legs and loosens the muscles to overcome fatigue.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (107, 'hitmonchan', 'fighting', 'none', 236, 47, 'Hitmonchan is said to possess the spirit of a boxer who had been working toward a world championship. This Pok&#233;mon has an indomitable spirit and will never give up in the face of adversity.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (108, 'lickitung', 'normal', 'none', 0, 48, 'Whenever Lickitung comes across something new&#44; it will unfailingly give it a lick. It does so because it memorizes things by texture and by taste. It is somewhat put off by sour things.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (109, 'koffing', 'poison', 'none', 0, 49, 'Koffing embodies toxic substances. It mixes the toxins with raw garbage to set off a chemical reaction that results in a terribly powerful poison gas. The higher the temperature&#44; the more gas is concocted by this Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (110, 'weezing', 'poison', 'none', 109, 49, 'Weezing alternately shrinks and inflates its twin bodies to mix together toxic gases inside. The more the gases are mixed&#44; the more powerful the toxins become. The Pok&#233;mon also becomes more putrid.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (111, 'rhyhorn', 'ground', 'rock', 0, 50, 'Rhyhorn’s brain is very small. It is so dense&#44; while on a run it forgets why it started running in the first place. It apparently remembers sometimes if it demolishes something.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (112, 'rhydon', 'ground', 'rock', 111, 50, 'Rhydon has a horn that serves as a drill. It is used for destroying rocks and boulders. This Pok&#233;mon occasionally rams into streams of magma&#44; but the armor-like hide prevents it from feeling the heat.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (113, 'chansey', 'normal', 'none', 440, 51, 'Chansey lays nutritionally excellent eggs on an everyday basis. The eggs are so delicious&#44; they are easily and eagerly devoured by even those people who have lost their appetite.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (114, 'tangela', 'grass', 'none', 0, 52, 'Tangela’s vines snap off easily if they are grabbed. This happens without pain&#44; allowing it to make a quick getaway. The lost vines are replaced by newly grown vines the very next day.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (115, 'kangaskhan', 'normal', 'none', 0, 53, 'If you come across a young Kangaskhan playing by itself&#44; you must never disturb it or attempt to catch it. The baby Pok&#233;mon’s parent is sure to be in the area&#44; and it will become violently enraged at you.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (116, 'horsea', 'water', 'none', 0, 54, 'If Horsea senses danger&#44; it will reflexively spray a dense black ink from its mouth and try to escape. This Pok&#233;mon swims by cleverly flapping the fin on its back.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (117, 'seadra', 'water', 'none', 116, 54, 'Seadra generates whirlpools by spinning its body. The whirlpools are strong enough to swallow even fishing boats. This Pok&#233;mon weakens prey with these currents&#44; then swallows it whole.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (118, 'goldeen', 'water', 'none', 0, 55, 'Goldeen loves swimming wild and free in rivers and ponds. If one of these Pok&#233;mon is placed in an aquarium&#44; it will shatter even the thickest glass with one ram of its horn and make its escape.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (119, 'seaking', 'water', 'none', 118, 55, 'Seaking is very protective of its eggs. The male and female will take turns patrolling around their nest and eggs. The guarding of eggs by these Pok&#233;mon goes on for over a month.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (120, 'staryu', 'water', 'none', 0, 56, 'Staryu apparently communicates with the stars in the night sky by flashing the red core at the center of its body. If parts of its body are torn&#44; this Pok&#233;mon simply regenerates the missing pieces and limbs.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (121, 'starmie', 'water', 'psychic', 120, 56, 'Starmie swims through water by spinning its star-shaped body as if it were a propeller on a ship. The core at the center of this Pok&#233;mon’s body glows in seven colors.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (122, 'mr-mime', 'psychic', 'fairy', 439, 57, 'Mr. Mime is a master of pantomime. Its gestures and motions convince watchers that something unseeable actually exists. Once the watchers are convinced&#44; the unseeable thing exists as if it were real.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (123, 'scyther', 'bug', 'flying', 0, 58, 'Scyther is blindingly fast. Its blazing speed enhances the effectiveness of the twin scythes on its forearms. This Pok&#233;mon’s scythes are so effective&#44; they can slice through thick logs in one wicked stroke.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (124, 'jynx', 'ice', 'psychic', 238, 59, 'Jynx walks rhythmically&#44; swaying and shaking its hips as if it were dancing. Its motions are so bouncingly alluring&#44; people seeing it are compelled to shake their hips without giving any thought to what they are doing.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (125, 'electabuzz', 'electric', 'none', 239, 60, 'When a storm arrives&#44; gangs of this Pok&#233;mon compete with each other to scale heights that are likely to be stricken by lightning bolts. Some towns use Electabuzz in place of lightning rods.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (126, 'magmar', 'fire', 'none', 240, 61, 'In battle&#44; Magmar blows out intensely hot flames from all over its body to intimidate its opponent. This Pok&#233;mon’s fiery bursts create heat waves that ignite grass and trees in its surroundings.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (127, 'pinsir', 'bug', 'none', 0, 62, 'Pinsir has a pair of massive horns. Protruding from the surface of these horns are thorns. These thorns are driven deeply into the foe’s body when the pincer closes&#44; making it tough for the foe to escape.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (128, 'tauros', 'normal', 'none', 0, 63, 'This Pok&#233;mon is not satisfied unless it is rampaging at all times. If there is no opponent for Tauros to battle&#44; it will charge at thick trees and knock them down to calm itself.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (129, 'magikarp', 'water', 'none', 0, 64, 'Magikarp is virtually useless in battle as it can only splash around. As a result&#44; it is considered to be weak. However&#44; it is actually a very hardy Pok&#233;mon that can survive in any body of water no matter how polluted it is.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (130, 'gyarados', 'water', 'flying', 129, 64, 'Once Gyarados goes on a rampage&#44; its ferociously violent blood doesn’t calm until it has burned everything down. There are records of this Pok&#233;mon’s rampages lasting a whole month.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (131, 'lapras', 'water', 'ice', 0, 65, 'People have driven Lapras almost to the point of extinction. In the evenings&#44; this Pok&#233;mon is said to sing plaintively as it seeks what few others of its kind still remain.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (132, 'ditto', 'normal', 'none', 0, 66, 'Ditto rearranges its cell structure to transform itself into other shapes. However&#44; if it tries to transform itself into something by relying on its memory&#44; this Pok&#233;mon manages to get details wrong.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (133, 'eevee', 'normal', 'none', 0, 67, 'Eevee has an unstable genetic makeup that suddenly mutates due to the environment in which it lives. Radiation from various stones causes this Pok&#233;mon to evolve.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (134, 'vaporeon', 'water', 'none', 133, 67, 'Vaporeon underwent a spontaneous mutation and grew fins and gills that allow it to live underwater. This Pok&#233;mon has the ability to freely control water.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (135, 'jolteon', 'electric', 'none', 133, 67, 'Jolteon’s cells generate a low level of electricity. This power is amplified by the static electricity of its fur&#44; enabling the Pok&#233;mon to drop thunderbolts. The bristling fur is made of electrically charged needles.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (136, 'flareon', 'fire', 'none', 133, 67, 'Flareon’s fluffy fur has a functional purpose—it releases heat into the air so that its body does not get excessively hot. This Pok&#233;mon’s body temperature can rise to a maximum of 1&#44;650 degrees Fahrenheit.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (137, 'porygon', 'normal', 'none', 0, 68, 'Porygon is capable of reverting itself entirely back to program data and entering cyberspace. This Pok&#233;mon is copy protected so it cannot be duplicated by copying.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (138, 'omanyte', 'rock', 'water', 0, 69, 'Omanyte is one of the ancient and long-since-extinct Pok&#233;mon that have been regenerated from fossils by people. If attacked by an enemy&#44; it withdraws itself inside its hard shell.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (139, 'omastar', 'rock', 'water', 138, 69, 'Omastar uses its tentacles to capture its prey. It is believed to have become extinct because its shell grew too large and heavy&#44; causing its movements to become too slow and ponderous.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (140, 'kabuto', 'rock', 'water', 0, 70, 'Kabuto is a Pok&#233;mon that has been regenerated from a fossil. However&#44; in extremely rare cases&#44; living examples have been discovered. The Pok&#233;mon has not changed at all for 300 million years.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (141, 'kabutops', 'rock', 'water', 140, 70, 'Kabutops swam underwater to hunt for its prey in ancient times. The Pok&#233;mon was apparently evolving from being a water dweller to living on land as evident from the beginnings of change in its gills and legs.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (142, 'aerodactyl', 'rock', 'flying', 0, 71, 'Aerodactyl is a Pok&#233;mon from the age of dinosaurs. It was regenerated from genetic material extracted from amber. It is imagined to have been the king of the skies in ancient times.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (143, 'snorlax', 'normal', 'none', 446, 72, 'Snorlax’s typical day consists of nothing more than eating and sleeping. It is such a docile Pok&#233;mon that there are children who use its expansive belly as a place to play.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (144, 'articuno', 'ice', 'flying', 0, 73, 'Articuno is a legendary bird Pok&#233;mon that can control ice. The flapping of its wings chills the air. As a result&#44; it is said that when this Pok&#233;mon flies&#44; snow will fall.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (145, 'zapdos', 'electric', 'flying', 0, 74, 'Zapdos is a legendary bird Pok&#233;mon that has the ability to control electricity. It usually lives in thunderclouds. The Pok&#233;mon gains power if it is stricken by lightning bolts.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (146, 'moltres', 'fire', 'flying', 0, 75, 'Moltres is a legendary bird Pok&#233;mon that has the ability to control fire. If this Pok&#233;mon is injured&#44; it is said to dip its body in the molten magma of a volcano to burn and heal itself.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (147, 'dratini', 'dragon', 'none', 0, 76, 'Dratini continually molts and sloughs off its old skin. It does so because the life energy within its body steadily builds to reach uncontrollable levels.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (148, 'dragonair', 'dragon', 'none', 147, 76, 'Dragonair stores an enormous amount of energy inside its body. It is said to alter weather conditions in its vicinity by discharging energy from the crystals on its neck and tail.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (149, 'dragonite', 'dragon', 'flying', 148, 76, 'Dragonite is capable of circling the globe in just 16 hours. It is a kindhearted Pok&#233;mon that leads lost and foundering ships in a storm to the safety of land.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (150, 'mewtwo', 'psychic', 'none', 0, 77, 'Mewtwo is a Pok&#233;mon that was created by genetic manipulation. However&#44; even though the scientific power of humans created this Pok&#233;mon’s body&#44; they failed to endow Mewtwo with a compassionate heart.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (151, 'mew', 'psychic', 'none', 0, 78, 'Mew is said to possess the genetic composition of all Pok&#233;mon. It is capable of making itself invisible at will&#44; so it entirely avoids notice even if it approaches people.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (152, 'chikorita', 'grass', 'none', 0, 79, 'In battle&#44; Chikorita waves its leaf around to keep the foe at bay. However&#44; a sweet fragrance also wafts from the leaf&#44; becalming the battling Pok&#233;mon and creating a cozy&#44; friendly atmosphere all around.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (153, 'bayleef', 'grass', 'none', 152, 79, 'Bayleef’s neck is ringed by curled-up leaves. Inside each tubular leaf is a small shoot of a tree. The fragrance of this shoot makes people peppy.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (154, 'meganium', 'grass', 'none', 153, 79, 'The fragrance of Meganium’s flower soothes and calms emotions. In battle&#44; this Pok&#233;mon gives off more of its becalming scent to blunt the foe’s fighting spirit.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (155, 'cyndaquil', 'fire', 'none', 0, 80, 'Cyndaquil protects itself by flaring up the flames on its back. The flames are vigorous if the Pok&#233;mon is angry. However&#44; if it is tired&#44; the flames splutter fitfully with incomplete combustion.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (156, 'quilava', 'fire', 'none', 155, 80, 'Quilava keeps its foes at bay with the intensity of its flames and gusts of superheated air. This Pok&#233;mon applies its outstanding nimbleness to dodge attacks even while scorching the foe with flames.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (157, 'typhlosion', 'fire', 'none', 156, 80, 'Typhlosion obscures itself behind a shimmering heat haze that it creates using its intensely hot flames. This Pok&#233;mon creates blazing explosive blasts that burn everything to cinders.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (158, 'totodile', 'water', 'none', 0, 81, 'Despite the smallness of its body&#44; Totodile’s jaws are very powerful. While the Pok&#233;mon may think it is just playfully nipping&#44; its bite has enough power to cause serious injury.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (159, 'croconaw', 'water', 'none', 158, 81, 'Once Croconaw has clamped its jaws on its foe&#44; it will absolutely not let go. Because the tips of its fangs are forked back like barbed fishhooks&#44; they become impossible to remove when they have sunk in.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (160, 'feraligatr', 'water', 'none', 159, 81, 'Feraligatr intimidates its foes by opening its huge mouth. In battle&#44; it will kick the ground hard with its thick and powerful hind legs to charge at the foe at an incredible speed.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (161, 'sentret', 'normal', 'none', 0, 82, 'When Sentret sleeps&#44; it does so while another stands guard. The sentry wakes the others at the first sign of danger. When this Pok&#233;mon becomes separated from its pack&#44; it becomes incapable of sleep due to fear.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (162, 'furret', 'normal', 'none', 161, 82, 'Furret has a very slim build. When under attack&#44; it can slickly squirm through narrow spaces and get away. In spite of its short limbs&#44; this Pok&#233;mon is very nimble and fleet.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (163, 'hoothoot', 'normal', 'flying', 0, 83, 'Hoothoot has an internal organ that senses and tracks the earth’s rotation. Using this special organ&#44; this Pok&#233;mon begins hooting at precisely the same time every day.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (164, 'noctowl', 'normal', 'flying', 163, 83, 'Noctowl never fails at catching prey in darkness. This Pok&#233;mon owes its success to its superior vision that allows it to see in minimal light&#44; and to its soft&#44; supple wings that make no sound in flight.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (165, 'ledyba', 'bug', 'flying', 0, 84, 'Ledyba secretes an aromatic fluid from where its legs join its body. This fluid is used for communicating with others. This Pok&#233;mon conveys its feelings to others by altering the fluid’s scent.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (166, 'ledian', 'bug', 'flying', 165, 84, 'It is said that in lands with clean air&#44; where the stars fill the sky&#44; there live Ledian in countless numbers. There is a good reason for this—the Pok&#233;mon uses the light of the stars as its energy.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (167, 'spinarak', 'bug', 'poison', 0, 85, 'The web spun by Spinarak can be considered its second nervous system. It is said that this Pok&#233;mon can determine what kind of prey is touching its web just by the tiny vibrations it feels through the web’s strands.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (168, 'ariados', 'bug', 'poison', 167, 85, 'Ariados’s feet are tipped with tiny hooked claws that enable it to scuttle on ceilings and vertical walls. This Pok&#233;mon constricts the foe with thin and strong silk webbing.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (169, 'crobat', 'poison', 'flying', 42, 17, 'Crobat sneaks up on its intended prey using wings that barely make a sound. This Pok&#233;mon rests by hanging on a tree branch with its rear legs that serve as wings.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (170, 'chinchou', 'water', 'electric', 0, 86, 'Chinchou’s two antennas are filled with cells that generate strong electricity. This Pok&#233;mon’s cells create so much electrical power&#44; it even makes itself tingle slightly.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (171, 'lanturn', 'water', 'electric', 170, 86, 'Lanturn is known to emit light. If you peer down into the dark sea from a ship at night&#44; you can sometimes see this Pok&#233;mon’s light rising from the depths where it swims. It gives the sea an appearance of a starlit night.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (172, 'pichu', 'electric', 'none', 0, 10, 'When Pichu plays with others&#44; it may short out electricity with another Pichu&#44; creating a shower of sparks. In that event&#44; this Pok&#233;mon will begin crying&#44; startled by the flash of sparks.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (173, 'cleffa', 'fairy', 'none', 0, 14, 'On nights with many shooting stars&#44; Cleffa can be seen dancing in a ring. They dance through the night and stop only at the break of day&#44; when these Pok&#233;mon quench their thirst with the morning dew.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (174, 'igglybuff', 'normal', 'fairy', 0, 16, 'Igglybuff has a soft and plushy body that feels very much like a marshmallow. From this body wafts a gently sweet fragrance that soothes and calms the emotions of its foes.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (175, 'togepi', 'fairy', 'none', 0, 87, 'As its energy&#44; Togepi uses the positive emotions of compassion and pleasure exuded by people and Pok&#233;mon. This Pok&#233;mon stores up feelings of happiness inside its shell&#44; then shares them with others.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (176, 'togetic', 'fairy', 'flying', 175, 87, 'Togetic is said to be a Pok&#233;mon that brings good fortune. When the Pok&#233;mon spots someone who is pure of heart&#44; it is said to appear and share its happiness with that person.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (177, 'natu', 'psychic', 'flying', 0, 88, 'Natu has a highly developed jumping ability. The Pok&#233;mon flaps and leaps onto tree branches that are taller than grown-up people to pick at the tree’s new shoots.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (178, 'xatu', 'psychic', 'flying', 177, 88, 'Xatu is known to stand motionless while staring at the sun all day long. Some people revere it as a mystical Pok&#233;mon out of their belief that Xatu is in possession of the power to see into the future.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (179, 'mareep', 'electric', 'none', 0, 89, 'Mareep’s fluffy coat of wool rubs together and builds a static charge. The more static electricity is charged&#44; the more brightly the lightbulb at the tip of its tail glows.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (180, 'flaaffy', 'electric', 'none', 179, 89, 'Flaaffy’s wool quality changes so that it can generate a high amount of static electricity with a small amount of wool. The bare and slick parts of its hide are shielded against electricity.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (181, 'ampharos', 'electric', 'none', 180, 89, 'Ampharos gives off so much light that it can be seen even from space. People in the old days used the light of this Pok&#233;mon to send signals back and forth with others far away.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (182, 'bellossom', 'grass', 'none', 44, 18, 'A Bellossom grows flowers more beautifully if it has evolved from a smelly Gloom—the more stinky the better. At night&#44; this Pok&#233;mon closes its petals and goes to sleep.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (183, 'marill', 'water', 'fairy', 298, 90, 'When fishing for food at the edge of a fast-running stream&#44; Marill wraps its tail around the trunk of a tree. This Pok&#233;mon’s tail is flexible and configured to stretch.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (184, 'azumarill', 'water', 'fairy', 183, 90, 'Azumarill can make balloons out of air. It makes these air balloons if it spots a drowning Pok&#233;mon. The air balloons enable the Pok&#233;mon in trouble to breathe.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (185, 'sudowoodo', 'rock', 'none', 438, 91, 'Sudowoodo camouflages itself as a tree to avoid being attacked by enemies. However&#44; because its hands remain green throughout the year&#44; the Pok&#233;mon is easily identified as a fake during the winter.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (186, 'politoed', 'water', 'none', 61, 26, 'The curled hair on Politoed’s head is proof of its status as a king. It is said that the longer and more curled the hair&#44; the more respect this Pok&#233;mon earns from its peers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (187, 'hoppip', 'grass', 'flying', 0, 92, 'This Pok&#233;mon drifts and floats with the wind. If it senses the approach of strong winds&#44; Hoppip links its leaves with other Hoppip to prepare against being blown away.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (188, 'skiploom', 'grass', 'flying', 187, 92, 'Skiploom’s flower blossoms when the temperature rises above 64 degrees Fahrenheit. How much the flower opens depends on the temperature. For that reason&#44; this Pok&#233;mon is sometimes used as a thermometer.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (189, 'jumpluff', 'grass', 'flying', 188, 92, 'Jumpluff rides warm southern winds to cross the sea and fly to foreign lands. The Pok&#233;mon descends to the ground when it encounters cold air while it is floating.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (190, 'aipom', 'normal', 'none', 0, 93, 'Aipom’s tail ends in a hand-like appendage that can be cleverly manipulated. However&#44; because the Pok&#233;mon uses its tail so much&#44; its real hands have become rather clumsy.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (191, 'sunkern', 'grass', 'none', 0, 94, 'Sunkern tries to move as little as it possibly can. It does so because it tries to conserve all the nutrients it has stored in its body for its evolution. It will not eat a thing&#44; subsisting only on morning dew.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (192, 'sunflora', 'grass', 'none', 191, 94, 'Sunflora converts solar energy into nutrition. It moves around actively in the daytime when it is warm. It stops moving as soon as the sun goes down for the night.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (193, 'yanma', 'bug', 'flying', 0, 95, 'Yanma is capable of seeing 360 degrees without having to move its eyes. It is a great flier that is adept at making sudden stops and turning midair. This Pok&#233;mon uses its flying ability to quickly chase down targeted prey.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (194, 'wooper', 'water', 'ground', 0, 96, 'Wooper usually lives in water. However&#44; it occasionally comes out onto land in search of food. On land&#44; it coats its body with a gooey&#44; toxic film.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (195, 'quagsire', 'water', 'ground', 194, 96, 'Quagsire hunts for food by leaving its mouth wide open in water and waiting for its prey to blunder in unaware. Because the Pok&#233;mon does not move&#44; it does not get very hungry.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (196, 'espeon', 'psychic', 'none', 133, 67, 'Espeon is extremely loyal to any Trainer it considers to be worthy. It is said that this Pok&#233;mon developed its precognitive powers to protect its Trainer from harm.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (197, 'umbreon', 'dark', 'none', 133, 67, 'Umbreon evolved as a result of exposure to the moon’s waves. It hides silently in darkness and waits for its foes to make a move. The rings on its body glow when it leaps to attack.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (198, 'murkrow', 'dark', 'flying', 0, 97, 'Murkrow was feared and loathed as the alleged bearer of ill fortune. This Pok&#233;mon shows strong interest in anything that sparkles or glitters. It will even try to steal rings from women.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (199, 'slowking', 'water', 'psychic', 79, 33, 'Slowking undertakes research every day in an effort to solve the mysteries of the world. However&#44; this Pok&#233;mon apparently forgets everything it has learned if the Shellder on its head comes off.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (200, 'misdreavus', 'ghost', 'none', 0, 98, 'Misdreavus frightens people with a creepy&#44; sobbing cry. The Pok&#233;mon apparently uses its red spheres to absorb the fearful feelings of foes and turn them into nutrition.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (201, 'unown', 'psychic', 'none', 0, 99, 'This Pok&#233;mon is shaped like ancient writing. It is a mystery as to which came first&#44; the ancient writings or the various Unown. Research into this topic is ongoing but nothing is known.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (202, 'wobbuffet', 'psychic', 'none', 360, 100, 'Wobbuffet does nothing but endure attacks—it won’t attack on its own. However&#44; it won’t endure an attack on its tail. When that happens&#44; the Pok&#233;mon will try to take the foe with it using Destiny Bond.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (203, 'girafarig', 'normal', 'psychic', 0, 101, 'Girafarig’s rear head contains a tiny brain that is too small for thinking. However&#44; the rear head doesn’t need to sleep&#44; so it can keep watch over its surroundings 24 hours a day.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (204, 'pineco', 'bug', 'none', 0, 102, 'Pineco hangs from a tree branch and patiently waits for prey to come along. If the Pok&#233;mon is disturbed while eating by someone shaking its tree&#44; it drops down to the ground and explodes with no warning.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (205, 'forretress', 'bug', 'steel', 204, 102, 'Forretress conceals itself inside its hardened steel shell. The shell is opened when the Pok&#233;mon is catching prey&#44; but it does so at such a quick pace that the shell’s inside cannot be seen.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (206, 'dunsparce', 'normal', 'none', 0, 103, 'Dunsparce has a drill for its tail. It uses this tail to burrow into the ground backward. This Pok&#233;mon is known to make its nest in complex shapes deep under the ground.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (207, 'gligar', 'ground', 'flying', 0, 104, 'Gligar glides through the air without a sound as if it were sliding. This Pok&#233;mon hangs on to the face of its foe using its clawed hind legs and the large pincers on its forelegs&#44; then injects the prey with its poison barb.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (208, 'steelix', 'steel', 'ground', 95, 41, 'Steelix lives even further underground than Onix. This Pok&#233;mon is known to dig toward the earth’s core. There are records of this Pok&#233;mon reaching a depth of over six-tenths of a mile underground.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (209, 'snubbull', 'fairy', 'none', 0, 105, 'By baring its fangs and making a scary face&#44; Snubbull sends smaller Pok&#233;mon scurrying away in terror. However&#44; this Pok&#233;mon seems a little sad at making its foes flee.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (210, 'granbull', 'fairy', 'none', 209, 105, 'Granbull has a particularly well-developed lower jaw. The enormous fangs are heavy&#44; causing the Pok&#233;mon to tip its head back for balance. Unless it is startled&#44; it will not try to bite indiscriminately.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (211, 'qwilfish', 'water', 'poison', 0, 106, 'Qwilfish sucks in water&#44; inflating itself. This Pok&#233;mon uses the pressure of the water it swallowed to shoot toxic quills all at once from all over its body. It finds swimming somewhat challenging.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (212, 'scizor', 'bug', 'steel', 123, 58, 'Scizor has a body with the hardness of steel. It is not easily fazed by ordinary sorts of attacks. This Pok&#233;mon flaps its wings to regulate its body temperature.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (213, 'shuckle', 'bug', 'rock', 0, 107, 'Shuckle quietly hides itself under rocks&#44; keeping its body concealed inside its hard shell while eating berries it has stored away. The berries mix with its body fluids to become a juice.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (214, 'heracross', 'bug', 'fighting', 0, 108, 'Heracross has sharp claws on its feet. These are planted firmly into the ground or the bark of a tree&#44; giving the Pok&#233;mon a secure and solid footing to forcefully fling away foes with its proud horn.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (215, 'sneasel', 'dark', 'ice', 0, 109, 'Sneasel scales trees by punching its hooked claws into the bark. This Pok&#233;mon seeks out unguarded nests and steals eggs for food while the parents are away.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (216, 'teddiursa', 'normal', 'none', 0, 110, 'This Pok&#233;mon likes to lick its palms that are sweetened by being soaked in honey. Teddiursa concocts its own honey by blending fruits and pollen collected by Beedrill.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (217, 'ursaring', 'normal', 'none', 216, 110, 'In the forests inhabited by Ursaring&#44; it is said that there are many streams and towering trees where they gather food. This Pok&#233;mon walks through its forest gathering food every day.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (218, 'slugma', 'fire', 'none', 0, 111, 'Slugma does not have any blood in its body. Instead&#44; intensely hot magma circulates throughout this Pok&#233;mon’s body&#44; carrying essential nutrients and oxygen to its organs.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (219, 'magcargo', 'fire', 'rock', 218, 111, 'Magcargo’s body temperature is approximately 18&#44;000 degrees Fahrenheit. Water is vaporized on contact. If this Pok&#233;mon is caught in the rain&#44; the raindrops instantly turn into steam&#44; cloaking the area in a thick fog.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (220, 'swinub', 'ice', 'ground', 0, 112, 'Swinub roots for food by rubbing its snout against the ground. Its favorite food is a mushroom that grows under the cover of dead grass. This Pok&#233;mon occasionally roots out hot springs.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (221, 'piloswine', 'ice', 'ground', 220, 112, 'Piloswine is covered by a thick coat of long hair that enables it to endure the freezing cold. This Pok&#233;mon uses its tusks to dig up food that has been buried under ice.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (222, 'corsola', 'water', 'rock', 0, 113, 'Clusters of Corsola congregate in warm seas where they serve as ideal hiding places for smaller Pok&#233;mon. When the water temperature falls&#44; this Pok&#233;mon migrates to the southern seas.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (223, 'remoraid', 'water', 'none', 0, 114, 'Remoraid sucks in water&#44; then expels it at high velocity using its abdominal muscles to shoot down flying prey. When evolution draws near&#44; this Pok&#233;mon travels downstream from rivers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (224, 'octillery', 'water', 'none', 223, 114, 'Octillery grabs onto its foe using its tentacles. This Pok&#233;mon tries to immobilize it before delivering the finishing blow. If the foe turns out to be too strong&#44; Octillery spews ink to escape.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (225, 'delibird', 'ice', 'flying', 0, 115, 'Delibird carries its food bundled up in its tail. There once was a famous explorer who managed to reach the peak of the world’s highest mountain&#44; thanks to one of these Pok&#233;mon sharing its food.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (226, 'mantine', 'water', 'flying', 458, 116, 'On sunny days&#44; schools of Mantine can be seen elegantly leaping over the sea’s waves. This Pok&#233;mon is not bothered by the Remoraid that hitches rides.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (227, 'skarmory', 'steel', 'flying', 0, 117, 'Skarmory’s steel wings become tattered and bashed in from repeated battles. Once a year&#44; the battered wings grow back completely&#44; restoring the cutting edges to their pristine state.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (228, 'houndour', 'dark', 'fire', 0, 118, 'Houndour hunt as a coordinated pack. They communicate with each other using a variety of cries to corner their prey. This Pok&#233;mon’s remarkable teamwork is unparalleled.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (229, 'houndoom', 'dark', 'fire', 228, 118, 'In a Houndoom pack&#44; the one with its horns raked sharply toward the back serves a leadership role. These Pok&#233;mon choose their leader by fighting among themselves.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (230, 'kingdra', 'water', 'dragon', 117, 54, 'Kingdra sleeps on the seafloor where it is otherwise devoid of life. When a storm arrives&#44; the Pok&#233;mon is said to awaken and wander about in search of prey.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (231, 'phanpy', 'ground', 'none', 0, 119, 'Phanpy uses its long nose to shower itself. When others gather around&#44; they thoroughly douse each other with water. These Pok&#233;mon can be seen drying their soaking-wet bodies at the edge of water.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (232, 'donphan', 'ground', 'none', 231, 119, 'If Donphan were to tackle with its hard body&#44; even a house could be destroyed. Using its massive strength&#44; the Pok&#233;mon helps clear rock and mud slides that block mountain trails.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (233, 'porygon2', 'normal', 'none', 137, 68, 'Porygon2 was created by humans using the power of science. The man-made Pok&#233;mon has been endowed with artificial intelligence that enables it to learn new gestures and emotions on its own.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (234, 'stantler', 'normal', 'none', 0, 120, 'Stantler’s magnificent antlers were traded at high prices as works of art. As a result&#44; this Pok&#233;mon was hunted close to extinction by those who were after the priceless antlers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (235, 'smeargle', 'normal', 'none', 0, 121, 'Smeargle marks the boundaries of its territory using a body fluid that leaks out from the tip of its tail. Over 5&#44;000 different marks left by this Pok&#233;mon have been found.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (236, 'tyrogue', 'fighting', 'none', 0, 47, 'Tyrogue becomes stressed out if it does not get to train every day. When raising this Pok&#233;mon&#44; the Trainer must establish and uphold various training methods.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (237, 'hitmontop', 'fighting', 'none', 236, 47, 'Hitmontop spins on its head at high speed&#44; all the while delivering kicks. This technique is a remarkable mix of both offense and defense at the same time. The Pok&#233;mon travels faster spinning than it does walking.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (238, 'smoochum', 'ice', 'psychic', 0, 59, 'Smoochum actively runs about&#44; but also falls quite often. Whenever the chance arrives&#44; it will look for its reflection to make sure its face hasn’t become dirty.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (239, 'elekid', 'electric', 'none', 0, 60, 'Elekid stores electricity in its body. If it touches metal and accidentally discharges all its built-up electricity&#44; this Pok&#233;mon begins swinging its arms in circles to recharge itself.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (240, 'magby', 'fire', 'none', 0, 61, 'Magby’s state of health is determined by observing the fire it breathes. If the Pok&#233;mon is spouting yellow flames from its mouth&#44; it is in good health. When it is fatigued&#44; black smoke will be mixed in with the flames.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (241, 'miltank', 'normal', 'none', 0, 122, 'Miltank gives over five gallons of milk on a daily basis. Its sweet milk is enjoyed by children and grown-ups alike. People who can’t drink milk turn it into yogurt and eat it instead.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (242, 'blissey', 'normal', 'none', 113, 51, 'Blissey senses sadness with its fluffy coat of fur. If it does so&#44; this Pok&#233;mon will rush over to a sad person&#44; no matter how far away&#44; to share a Lucky Egg that brings a smile to any face.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (243, 'raikou', 'electric', 'none', 0, 123, 'Raikou embodies the speed of lightning. The roars of this Pok&#233;mon send shock waves shuddering through the air and shake the ground as if lightning bolts had come crashing down.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (244, 'entei', 'fire', 'none', 0, 124, 'Entei embodies the passion of magma. This Pok&#233;mon is thought to have been born in the eruption of a volcano. It sends up massive bursts of fire that utterly consume all that they touch.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (245, 'suicune', 'water', 'none', 0, 125, 'Suicune embodies the compassion of a pure spring of water. It runs across the land with gracefulness. This Pok&#233;mon has the power to purify dirty water.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (246, 'larvitar', 'rock', 'ground', 0, 126, 'Larvitar is born deep under the ground. To come up to the surface&#44; this Pok&#233;mon must eat its way through the soil above. Until it does so&#44; Larvitar cannot see its parents.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (247, 'pupitar', 'rock', 'ground', 246, 126, 'Pupitar creates a gas inside its body that it compresses and forcefully ejects to propel itself like a jet. The body is very durable—it avoids damage even if it hits solid steel.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (248, 'tyranitar', 'rock', 'dark', 247, 126, 'Tyranitar is so overwhelmingly powerful&#44; it can bring down a whole mountain to make its nest. This Pok&#233;mon wanders about in mountains seeking new opponents to fight.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (249, 'lugia', 'psychic', 'flying', 0, 127, 'Lugia’s wings pack devastating power—a light fluttering of its wings can blow apart regular houses. As a result&#44; this Pok&#233;mon chooses to live out of sight deep under the sea.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (250, 'ho-oh', 'fire', 'flying', 0, 128, 'Ho-Oh’s feathers glow in seven colors depending on the angle at which they are struck by light. These feathers are said to bring happiness to the bearers. This Pok&#233;mon is said to live at the foot of a rainbow.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (251, 'celebi', 'psychic', 'grass', 0, 129, 'This Pok&#233;mon came from the future by crossing over time. It is thought that so long as Celebi appears&#44; a bright and shining future awaits us.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (252, 'treecko', 'grass', 'none', 0, 130, 'Treecko is cool&#44; calm&#44; and collected—it never panics under any situation. If a bigger foe were to glare at this Pok&#233;mon&#44; it would glare right back without conceding an inch of ground.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (253, 'grovyle', 'grass', 'none', 252, 130, 'This Pok&#233;mon adeptly flies from branch to branch in trees. In a forest&#44; no Pok&#233;mon can ever hope to catch a fleeing Grovyle however fast they may be.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (254, 'sceptile', 'grass', 'none', 253, 130, 'Sceptile has seeds growing on its back. They are said to be bursting with nutrients that revitalize trees. This Pok&#233;mon raises the trees in a forest with loving care.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (255, 'torchic', 'fire', 'none', 0, 131, 'Torchic has a place inside its body where it keeps its flame. Give it a hug—it will be glowing with warmth. This Pok&#233;mon is covered all over by a fluffy coat of down.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (256, 'combusken', 'fire', 'fighting', 255, 131, 'Combusken battles with the intensely hot flames it spews from its beak and with outstandingly destructive kicks. This Pok&#233;mon’s cry is very loud and distracting.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (257, 'blaziken', 'fire', 'fighting', 256, 131, 'Blaziken has incredibly strong legs—it can easily clear a 30-story building in one leap. This Pok&#233;mon’s blazing punches leave its foes scorched and blackened.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (258, 'mudkip', 'water', 'none', 0, 132, 'In water&#44; Mudkip breathes using the gills on its cheeks. If it is faced with a tight situation in battle&#44; this Pok&#233;mon will unleash its amazing power—it can crush rocks bigger than itself.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (259, 'marshtomp', 'water', 'ground', 258, 132, 'Marshtomp is much faster at traveling through mud than it is at swimming. This Pok&#233;mon’s hindquarters exhibit obvious development&#44; giving it the ability to walk on just its hind legs.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (260, 'swampert', 'water', 'ground', 259, 132, 'Swampert predicts storms by sensing subtle differences in the sounds of waves and tidal winds with its fins. If a storm is approaching&#44; it piles up boulders to protect itself.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (261, 'poochyena', 'dark', 'none', 0, 133, 'Poochyena is an omnivore—it will eat anything. A distinguishing feature is how large its fangs are compared to its body. This Pok&#233;mon tries to intimidate its foes by making the hair on its tail bristle out.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (262, 'mightyena', 'dark', 'none', 261, 133, 'Mightyena travel and act as a pack in the wild. The memory of its life in the wild compels the Pok&#233;mon to obey only those Trainers that it recognizes to possess superior skill.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (263, 'zigzagoon', 'normal', 'none', 0, 134, 'The hair on Zigzagoon’s back is bristly. It rubs the hard back hair against trees to leave its territorial markings. This Pok&#233;mon may play dead to fool foes in battle.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (264, 'linoone', 'normal', 'none', 263, 134, 'When hunting&#44; Linoone will make a beeline straight for the prey at a full run. While this Pok&#233;mon is capable of topping 60 mph&#44; it has to come to a screeching halt before it can turn.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (265, 'wurmple', 'bug', 'none', 0, 135, 'Wurmple is targeted by Swellow as prey. This Pok&#233;mon will try to resist by pointing the spikes on its rear at the attacking predator. It will weaken the foe by leaking poison from the spikes.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (266, 'silcoon', 'bug', 'none', 265, 135, 'Silcoon was thought to endure hunger and not consume anything before its evolution. However&#44; it is now thought that this Pok&#233;mon slakes its thirst by drinking rainwater that collects on its silk.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (267, 'beautifly', 'bug', 'flying', 266, 135, 'Beautifly has a long mouth like a coiled needle&#44; which is very convenient for collecting pollen from flowers. This Pok&#233;mon rides the spring winds as it flits around gathering pollen.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (268, 'cascoon', 'bug', 'none', 265, 135, 'If it is attacked&#44; Cascoon remains motionless however badly it may be hurt. It does so because if it were to move&#44; its body would be weak upon evolution. This Pok&#233;mon will also not forget the pain it endured.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (269, 'dustox', 'bug', 'poison', 268, 135, 'When Dustox flaps its wings&#44; a fine dust is scattered all over. This dust is actually a powerful poison that will even make a pro wrestler sick. This Pok&#233;mon searches for food using its antennae like radar.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (270, 'lotad', 'water', 'grass', 0, 136, 'Lotad is said to have dwelled on land before. However&#44; this Pok&#233;mon is thought to have returned to water because the leaf on its head grew large and heavy. It now lives by floating atop the water.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (271, 'lombre', 'water', 'grass', 270, 136, 'Lombre’s entire body is covered by a slippery&#44; slimy film. It feels horribly unpleasant to be touched by this Pok&#233;mon’s hands. Lombre is often mistaken for a human child.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (272, 'ludicolo', 'water', 'grass', 271, 136, 'Upon hearing an upbeat and cheerful rhythm&#44; the cells in Ludicolo’s body become very energetic and active. Even in battle&#44; this Pok&#233;mon will exhibit an amazing amount of power.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (273, 'seedot', 'grass', 'none', 0, 137, 'Seedot looks exactly like an acorn when it is dangling from a tree branch. It startles other Pok&#233;mon by suddenly moving. This Pok&#233;mon polishes its body once a day using leaves.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (274, 'nuzleaf', 'grass', 'dark', 273, 137, 'This Pok&#233;mon pulls out the leaf on its head and makes a flute with it. The sound of Nuzleaf’s flute strikes fear and uncertainty in the hearts of people lost in a forest.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (275, 'shiftry', 'grass', 'dark', 274, 137, 'Shiftry’s large fans generate awesome gusts of wind at a speed close to 100 feet per second. The whipped-up wind blows anything away. This Pok&#233;mon chooses to live quietly deep in forests.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (276, 'taillow', 'normal', 'flying', 0, 138, 'Taillow is young—it has only just left its nest. As a result&#44; it sometimes becomes lonesome and cries at night. This Pok&#233;mon feeds on Wurmple that live in forests.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (277, 'swellow', 'normal', 'flying', 276, 138, 'Swellow is very conscientious about the upkeep of its glossy wings. Once two Swellow are gathered&#44; they diligently take care of cleaning each other’s wings.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (278, 'wingull', 'water', 'flying', 0, 139, 'Wingull rides updrafts rising from the sea by extending its long and narrow wings to glide. This Pok&#233;mon’s long beak is useful for catching prey.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (279, 'pelipper', 'water', 'flying', 278, 139, 'Pelipper searches for food while in flight by skimming the wave tops. This Pok&#233;mon dips its large bill in the sea to scoop up food&#44; then swallows everything in one big gulp.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (280, 'ralts', 'psychic', 'fairy', 0, 140, 'Ralts has the ability to sense the emotions of people. If its Trainer is in a cheerful mood&#44; this Pok&#233;mon grows cheerful and joyous in the same way.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (281, 'kirlia', 'psychic', 'fairy', 280, 140, 'Kirlia uses the horns on its head to amplify its psychokinetic power. When the Pok&#233;mon uses its power&#44; the air around it becomes distorted&#44; creating mirages of nonexistent scenery.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (282, 'gardevoir', 'psychic', 'fairy', 281, 140, 'Gardevoir has the psychokinetic power to distort the dimensions and create a small black hole. This Pok&#233;mon will try to protect its Trainer even at the risk of its own life.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (283, 'surskit', 'bug', 'water', 0, 141, 'If Surskit senses danger&#44; it secretes a thick&#44; sugary syrup from the tip of its head. There are some Pok&#233;mon that love eating this syrup.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (284, 'masquerain', 'bug', 'flying', 283, 141, 'Masquerain’s antennas have eyelike patterns that usually give it an angry look. If the “eyes” are droopy and appear sad&#44; it is said to be a sign that a heavy rainfall is on its way.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (285, 'shroomish', 'grass', 'none', 0, 142, 'If Shroomish senses danger&#44; it shakes its body and scatters spores from the top of its head. This Pok&#233;mon’s spores are so toxic&#44; they make trees and weeds wilt.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (286, 'breloom', 'grass', 'fighting', 285, 142, 'The seeds ringing Breloom’s tail are made of hardened toxic spores. It is horrible to eat the seeds. Just taking a bite of this Pok&#233;mon’s seed will cause your stomach to rumble.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (287, 'slakoth', 'normal', 'none', 0, 143, 'Slakoth’s heart beats just once a minute. Whatever happens&#44; it is content to loaf around motionless. It is rare to see this Pok&#233;mon in motion.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (288, 'vigoroth', 'normal', 'none', 287, 143, 'Vigoroth is simply incapable of remaining still. Even when it tries to sleep&#44; the blood in its veins grows agitated&#44; compelling this Pok&#233;mon to run wild throughout the jungle before it can settle down.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (289, 'slaking', 'normal', 'none', 288, 143, 'Wherever Slaking live&#44; rings of over a yard in diameter appear in grassy fields. They are made by the Pok&#233;mon as it eats all the grass within reach while lying prone on the ground.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (290, 'nincada', 'bug', 'ground', 0, 144, 'Nincada lives underground. It uses its sharp claws to carve the roots of trees and absorb moisture and nutrients. This Pok&#233;mon can’t withstand bright sunlight so avoids it.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (291, 'ninjask', 'bug', 'flying', 290, 144, 'If Ninjask is not trained properly&#44; it will refuse to obey the Trainer and cry loudly continuously. Because of this quality&#44; this Pok&#233;mon is said to be one that puts the Trainer’s abilities to the test.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (292, 'shedinja', 'bug', 'ghost', 290, 144, 'Shedinja is a peculiar Pok&#233;mon. It seems to appear unsought in a Pok&#233; Ball after a Nincada evolves. This bizarre Pok&#233;mon is entirely immobile—it doesn’t even breathe.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (293, 'whismur', 'normal', 'none', 0, 145, 'Whismur is very timid. If it starts to cry loudly&#44; it becomes startled by its own crying and cries even harder. When it finally stops crying&#44; the Pok&#233;mon goes to sleep&#44; all tired out.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (294, 'loudred', 'normal', 'none', 293, 145, 'Loudred shouts while stamping its feet. After it finishes shouting&#44; this Pok&#233;mon becomes incapable of hearing anything for a while. This is considered to be a weak point.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (295, 'exploud', 'normal', 'none', 294, 145, 'Exploud communicates its feelings to the others by emitting whistle-like sounds from the tubes on its body. This Pok&#233;mon only raises its voice when it is in battle.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (296, 'makuhita', 'fighting', 'none', 0, 146, 'Makuhita has a tireless spirit—it will never give up hope. It eats a lot of food&#44; gets plenty of sleep&#44; and it trains very rigorously. By living that way&#44; this Pok&#233;mon packs its body with energy.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (297, 'hariyama', 'fighting', 'none', 296, 146, 'Hariyama’s thick body may appear fat&#44; but it is actually a hunk of solid muscle. If this Pok&#233;mon bears down and tightens all its muscles&#44; its body becomes as hard as a rock.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (298, 'azurill', 'normal', 'fairy', 0, 90, 'Azurill’s tail is large and bouncy. It is packed full of the nutrients this Pok&#233;mon needs to grow. Azurill can be seen bouncing and playing on its big&#44; rubbery tail.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (299, 'nosepass', 'rock', 'none', 0, 147, 'Nosepass had been said to be completely unmoving&#44; with its magnetic nose pointed due north. However&#44; close observation has revealed that the Pok&#233;mon actually moves by a little over 3/8 of an inch every year.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (300, 'skitty', 'normal', 'none', 0, 148, 'Skitty is known to chase around playfully after its own tail. In the wild&#44; this Pok&#233;mon lives in holes in the trees of forests. It is very popular as a pet because of its adorable looks.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (301, 'delcatty', 'normal', 'none', 300, 148, 'Delcatty sleeps anywhere it wants without keeping a permanent nest. If other Pok&#233;mon approach it as it sleeps&#44; this Pok&#233;mon will never fight—it will just move away somewhere else.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (302, 'sableye', 'dark', 'ghost', 0, 149, 'Sableye digs the ground with sharpened claws to find rocks that it eats. Substances in the eaten rocks crystallize and rise up to the Pok&#233;mon’s body surface.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (303, 'mawile', 'steel', 'fairy', 0, 150, 'Don’t be taken in by this Pok&#233;mon’s cute face—it’s very dangerous. Mawile fools the foe into letting down its guard&#44; then chomps down with its massive jaws. The steel jaws are really horns that have been transformed.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (304, 'aron', 'steel', 'rock', 0, 151, 'Aron has a body of steel. With one all-out charge&#44; this Pok&#233;mon can demolish even a heavy dump truck. The destroyed dump truck then becomes a handy meal for the Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (305, 'lairon', 'steel', 'rock', 304, 151, 'Lairon feeds on iron contained in rocks and water. It makes its nest on mountains where iron ore is buried. As a result&#44; the Pok&#233;mon often clashes with humans mining the iron ore.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (306, 'aggron', 'steel', 'rock', 305, 151, 'Aggron is protective of its environment. If its mountain is ravaged by a landslide or a fire&#44; this Pok&#233;mon will haul topsoil to the area&#44; plant trees&#44; and beautifully restore its own territory.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (307, 'meditite', 'fighting', 'psychic', 0, 152, 'Meditite heightens its inner energy through meditation. It survives on just one berry a day. Minimal eating is another aspect of this Pok&#233;mon’s training.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (308, 'medicham', 'fighting', 'psychic', 307, 152, 'Through the power of meditation&#44; Medicham developed its sixth sense. It gained the ability to use psychokinetic powers. This Pok&#233;mon is known to meditate for a whole month without eating.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (309, 'electrike', 'electric', 'none', 0, 153, 'Electrike runs faster than the human eye can follow. The friction from running is converted into electricity&#44; which is then stored in this Pok&#233;mon’s fur.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (310, 'manectric', 'electric', 'none', 309, 153, 'Manectric discharges strong electricity from its mane. The mane is used for collecting electricity in the atmosphere. This Pok&#233;mon creates thunderclouds above its head.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (311, 'plusle', 'electric', 'none', 0, 154, 'When Plusle is cheering on its partner&#44; it flashes with electric sparks from all over its body. If its partner loses&#44; this Pok&#233;mon cries loudly.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (312, 'minun', 'electric', 'none', 0, 155, 'Minun loves to cheer on its partner in battle. It gives off sparks from its body while it is doing so. If its partner is in trouble&#44; this Pok&#233;mon gives off increasing amounts of sparks.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (313, 'volbeat', 'bug', 'none', 0, 156, 'Volbeat’s tail glows like a lightbulb. With other Volbeat&#44; it uses its tail to draw geometric shapes in the night sky. This Pok&#233;mon loves the sweet aroma given off by Illumise.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (314, 'illumise', 'bug', 'none', 0, 157, 'Illumise leads a flight of illuminated Volbeat to draw signs in the night sky. This Pok&#233;mon is said to earn greater respect from its peers by composing more complex designs in the sky.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (315, 'roselia', 'grass', 'poison', 406, 158, 'On extremely rare occasions&#44; a Roselia is said to appear with its flowers in unusual colors. The thorns on this Pok&#233;mon’s head contain a vicious poison.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (316, 'gulpin', 'poison', 'none', 0, 159, 'Most of Gulpin’s body is made up of its stomach—its heart and brain are very small in comparison. This Pok&#233;mon’s stomach contains special enzymes that dissolve anything.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (317, 'swalot', 'poison', 'none', 316, 159, 'Swalot has no teeth&#44; so what it eats&#44; it swallows whole&#44; no matter what. Its cavernous mouth yawns widely. An automobile tire could easily fit inside this Pok&#233;mon’s mouth.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (318, 'carvanha', 'water', 'dark', 0, 160, 'If anything invades Carvanha’s territory&#44; it will swarm and tear at the intruder with its pointed fangs. On its own&#44; however&#44; this Pok&#233;mon turns suddenly timid.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (319, 'sharpedo', 'water', 'dark', 318, 160, 'Sharpedo can swim at speeds of up to 75 mph by jetting seawater out of its backside. This Pok&#233;mon’s drawback is its inability to swim long distances.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (320, 'wailmer', 'water', 'none', 0, 161, 'Wailmer can store water inside its body to transform itself into a ball for bouncing around on the ground. By filling itself up with more water&#44; this Pok&#233;mon can elevate the height of its bounces.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (321, 'wailord', 'water', 'none', 320, 161, 'When chasing prey&#44; Wailord herds them by leaping out of the water and making a humongous splash. It is breathtaking to see this Pok&#233;mon leaping out of the sea with others in its pod.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (322, 'numel', 'fire', 'ground', 0, 162, 'Numel stores magma of almost 2&#44;200 degrees Fahrenheit within its body. If it gets wet&#44; the magma cools and hardens. In that event&#44; the Pok&#233;mon’s body grows heavy and its movements become sluggish.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (323, 'camerupt', 'fire', 'ground', 322, 162, 'The humps on Camerupt’s back are formed by a transformation of its bones. They sometimes blast out molten magma. This Pok&#233;mon apparently erupts often when it is enraged.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (324, 'torkoal', 'fire', 'none', 0, 163, 'Torkoal generates energy by burning coal. It grows weaker as the fire dies down. When it is preparing for battle&#44; this Pok&#233;mon burns more coal.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (325, 'spoink', 'psychic', 'none', 0, 164, 'Spoink keeps a pearl on top of its head. The pearl functions to amplify this Pok&#233;mon’s psychokinetic powers. It is therefore on a constant search for a bigger pearl.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (326, 'grumpig', 'psychic', 'none', 325, 164, 'Grumpig uses the black pearls on its body to wield its fantastic powers. When it is doing so&#44; it dances bizarrely. This Pok&#233;mon’s black pearls are valuable as works of art.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (327, 'spinda', 'normal', 'none', 0, 165, 'No two Spinda are said to have identical spot patterns on their hides. This Pok&#233;mon moves in a curious manner as if it is stumbling in dizziness. Its lurching movements can cause the opponent to become confused.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (328, 'trapinch', 'ground', 'none', 0, 166, 'Trapinch is a patient hunter. It digs an inescapable pit in a desert and waits for its prey to come tumbling down. This Pok&#233;mon can go a whole week without access to any water.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (329, 'vibrava', 'ground', 'dragon', 328, 166, 'Vibrava’s wings have not yet completed the process of growing. Rather than flying long distances&#44; they are more useful for generating ultrasonic waves by vibrating.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (330, 'flygon', 'ground', 'dragon', 329, 166, 'Flygon whips up a sandstorm by flapping its wings. The wings create a series of notes that sound like singing. Because the “singing” is the only thing that can be heard in a sandstorm&#44; this Pok&#233;mon is said to be the desert spirit.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (331, 'cacnea', 'grass', 'none', 0, 167, 'The more arid and harsh the environment&#44; the more pretty and fragrant a flower Cacnea grows. This Pok&#233;mon battles by wildly swinging its thorny arms.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (332, 'cacturne', 'grass', 'dark', 331, 167, 'If a traveler is going through a desert in the thick of night&#44; Cacturne will follow in a ragtag group. The Pok&#233;mon are biding their time&#44; waiting for the traveler to tire and become incapable of moving.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (333, 'swablu', 'normal', 'flying', 0, 168, 'Swablu loves to make things clean. If it spots something dirty&#44; it will wipe and polish it with its cottony wings. If its wings become dirty&#44; this Pok&#233;mon finds a stream and showers itself.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (334, 'altaria', 'dragon', 'flying', 333, 168, 'Altaria sings in a gorgeous soprano. Its wings are like cotton clouds. This Pok&#233;mon catches updrafts with its buoyant wings and soars way up into the wild blue yonder.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (335, 'zangoose', 'normal', 'none', 0, 169, 'Zangoose usually stays on all fours&#44; but when angered&#44; it gets up on its hind legs and extends its claws. This Pok&#233;mon shares a bitter rivalry with Seviper that dates back over generations.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (336, 'seviper', 'poison', 'none', 0, 170, 'Seviper’s swordlike tail serves two purposes—it slashes foes and douses them with secreted poison. This Pok&#233;mon will not give up its long-running blood feud with Zangoose.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (337, 'lunatone', 'rock', 'psychic', 0, 171, 'Lunatone becomes active around the time of the full moon. Instead of walking&#44; it moves by floating in midair. The Pok&#233;mon’s intimidating red eyes cause all those who see it to become transfixed with fear.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (338, 'solrock', 'rock', 'psychic', 0, 172, 'Sunlight is the source of Solrock’s power. It is said to possess the ability to read the emotions of others. This Pok&#233;mon gives off intense heat while rotating its body.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (339, 'barboach', 'water', 'ground', 0, 173, 'Barboach’s body is covered with a slimy film. If a foe grabs it&#44; this Pok&#233;mon just slips out of the enemy’s grip. This Pok&#233;mon grows weak if the slimy coating dries up.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (340, 'whiscash', 'water', 'ground', 339, 173, 'If Whiscash goes on a wild rampage&#44; it sets off a quake-like tremor with a radius of over three miles. This Pok&#233;mon has the ability to predict real earthquakes.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (341, 'corphish', 'water', 'none', 0, 174, 'Corphish catches prey with its sharp claws. It has no likes or dislikes when it comes to food—it will eat anything. This Pok&#233;mon has no trouble living in filthy water.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (342, 'crawdaunt', 'water', 'dark', 341, 174, 'Crawdaunt molts (sheds) its shell regularly. Immediately after molting&#44; its shell is soft and tender. Until the shell hardens&#44; this Pok&#233;mon hides in its streambed burrow to avoid attack from its foes.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (343, 'baltoy', 'ground', 'psychic', 0, 175, 'As soon as it spots others of its kind&#44; Baltoy congregates with them and then begins crying noisily in unison. This Pok&#233;mon sleeps while cleverly balancing itself on its one foot.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (344, 'claydol', 'ground', 'psychic', 343, 175, 'Claydol is an enigma that appeared from a clay statue made by an ancient civilization dating back 20&#44;000 years. This Pok&#233;mon shoots beams from both its hands.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (345, 'lileep', 'rock', 'grass', 0, 176, 'Lileep is an ancient Pok&#233;mon that was regenerated from a fossil. It remains permanently anchored to a rock. From its immobile perch&#44; this Pok&#233;mon intently scans for prey with its two eyes.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (346, 'cradily', 'rock', 'grass', 345, 176, 'Cradily’s body serves as an anchor&#44; preventing it from being washed away in rough seas. This Pok&#233;mon secretes a strong digestive fluid from its tentacles.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (347, 'anorith', 'rock', 'bug', 0, 177, 'Anorith is said to be a type of Pok&#233;mon predecessor&#44; with eight wings at the sides of its body. This Pok&#233;mon swam in the primordial sea by undulating these eight wings.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (348, 'armaldo', 'rock', 'bug', 347, 177, 'Armaldo is a Pok&#233;mon species that became extinct in prehistoric times. This Pok&#233;mon is said to have walked on its hind legs&#44; which would have been more convenient for life on land.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (349, 'feebas', 'water', 'none', 0, 178, 'While Feebas’s body is in tatters&#44; it has a hardy and tenacious life force that enables it to live anywhere. However&#44; this Pok&#233;mon is also slow and dimwitted&#44; making it an easy catch.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (350, 'milotic', 'water', 'none', 349, 178, 'Milotic live at the bottom of large lakes. When this Pok&#233;mon’s body glows a vivid pink&#44; it releases a pulsing wave of energy that brings soothing calm to troubled hearts.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (351, 'castform', 'normal', 'none', 0, 179, 'Castform borrows the power of nature to transform itself into the guises of the sun&#44; rain&#44; and snow-clouds. This Pok&#233;mon’s feelings change with the weather.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (352, 'kecleon', 'normal', 'none', 0, 180, 'Kecleon alters its body coloration to blend in with its surroundings&#44; allowing it to sneak up on its prey unnoticed. Then it lashes out with its long&#44; stretchy tongue to instantly ensnare the unsuspecting target.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (353, 'shuppet', 'ghost', 'none', 0, 181, 'Shuppet grows by feeding on dark emotions&#44; such as vengefulness and envy&#44; in the hearts of people. It roams through cities in search of grudges that taint people.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (354, 'banette', 'ghost', 'none', 353, 181, 'A cursed energy permeated the stuffing of a discarded and forgotten plush doll&#44; giving it new life as Banette. The Pok&#233;mon’s energy would escape if it were to ever open its mouth.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (355, 'duskull', 'ghost', 'none', 0, 182, 'Duskull wanders lost among the deep darkness of midnight. There is an oft-told admonishment given to misbehaving children that this Pok&#233;mon will spirit away bad children who earn scoldings from their mothers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (356, 'dusclops', 'ghost', 'none', 355, 182, 'Dusclops absorbs anything&#44; however large the object may be. This Pok&#233;mon hypnotizes its foe by waving its hands in a macabre manner and by bringing its single eye to bear. The hypnotized foe is made to do Dusclops’s bidding.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (357, 'tropius', 'grass', 'flying', 0, 183, 'Children of the southern tropics eat as snacks the fruit that grows in bunches around the neck of Tropius. This Pok&#233;mon flies by flapping the leaves on its back as if they were wings.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (358, 'chimecho', 'psychic', 'none', 433, 184, 'In high winds&#44; Chimecho cries as it hangs from a tree branch or the eaves of a building using a suction cup on its head. This Pok&#233;mon plucks berries with its long tail and eats them.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (359, 'absol', 'dark', 'none', 0, 185, 'Absol has the ability to foretell the coming of natural disasters. It lives in a harsh&#44; rugged mountain environment. This Pok&#233;mon very rarely ventures down from the mountains.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (360, 'wynaut', 'psychic', 'none', 0, 100, 'Wynaut gather on moonlit nights to play by squeezing up against each other. By being squeezed&#44; this Pok&#233;mon gains endurance and is trained to dole out powerful counterattacks.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (361, 'snorunt', 'ice', 'none', 0, 186, 'Snorunt survives by eating only snow and ice. Old folklore claims that a house visited by this Pok&#233;mon is sure to prosper for many generations to come.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (362, 'glalie', 'ice', 'none', 361, 186, 'Glalie has the ability to freely control ice. For example&#44; it can instantly freeze its prey solid. After immobilizing its prey in ice&#44; this Pok&#233;mon enjoys eating it in leisurely fashion.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (363, 'spheal', 'ice', 'water', 0, 187, 'Spheal always travels by rolling around on its ball-like body. When the season for ice floes arrives&#44; this Pok&#233;mon can be seen rolling about on ice and crossing the sea.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (364, 'sealeo', 'ice', 'water', 363, 187, 'Sealeo often balances and rolls things on the tip of its nose. While the Pok&#233;mon is rolling something&#44; it checks the object’s aroma and texture to determine whether it likes the object or not.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (365, 'walrein', 'ice', 'water', 364, 187, 'Walrein swims all over in frigid seawater while crushing icebergs with its grand&#44; imposing tusks. Its thick layer of blubber makes enemy attacks bounce off harmlessly.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (366, 'clamperl', 'water', 'none', 0, 188, 'Clamperl grows while being protected by its rock-hard shell. When its body becomes too large to fit inside the shell&#44; it is sure evidence that this Pok&#233;mon is getting close to evolution.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (367, 'huntail', 'water', 'none', 366, 188, 'Huntail’s tail is shaped like a fish. It uses the tail to attract prey&#44; then swallows the prey whole with its large&#44; gaping mouth. This Pok&#233;mon swims by wiggling its slender body like a snake.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (368, 'gorebyss', 'water', 'none', 366, 188, 'Although Gorebyss is the very picture of elegance and beauty while swimming&#44; it is also cruel. When it spots prey&#44; this Pok&#233;mon inserts its thin mouth into the prey’s body and drains the prey of its body fluids.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (369, 'relicanth', 'water', 'rock', 0, 189, 'Relicanth is a rare species that was discovered in deep-sea explorations. This Pok&#233;mon’s body withstands the enormous water pressure of the ocean depths. Its body is covered in tough scales that are like craggy rocks.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (370, 'luvdisc', 'water', 'none', 0, 190, 'Luvdisc’s heart-shaped body is a symbol of love and romance. It is said that any couple meeting this Pok&#233;mon is promised a loving relationship that never ends.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (371, 'bagon', 'dragon', 'none', 0, 191, 'Bagon harbors a never-ending dream of one day soaring high among the clouds. As if trying to dispel its frustration over its inability to fly&#44; this Pok&#233;mon slams its hard head against huge rocks and shatters them into pebbles.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (372, 'shelgon', 'dragon', 'none', 371, 191, 'Covering Shelgon’s body are outgrowths much like bones. The shell is very hard and bounces off enemy attacks. When awaiting evolution&#44; this Pok&#233;mon hides away in a cavern.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (373, 'salamence', 'dragon', 'flying', 372, 191, 'By evolving into Salamence&#44; this Pok&#233;mon finally realizes its long-held dream of growing wings. To express its joy&#44; it flies and wheels all over the sky while spouting flames from its mouth.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (374, 'beldum', 'steel', 'psychic', 0, 192, 'Beldum keeps itself floating by generating a magnetic force that repels earth’s natural magnetism. When it sleeps&#44; this Pok&#233;mon anchors itself to a cliff using the hooks on its rear.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (375, 'metang', 'steel', 'psychic', 374, 192, 'When two Beldum fuse together&#44; Metang is formed. The brains of the Beldum are joined by a magnetic nervous system. This Pok&#233;mon turns its arms to the rear for traveling at high speed.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (376, 'metagross', 'steel', 'psychic', 375, 192, 'Metagross is the result of two Metang achieving fusion. When hunting&#44; this Pok&#233;mon pins the prey to the ground under its massive body. It then eats the helpless victim using the large mouth on its stomach.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (377, 'regirock', 'rock', 'none', 0, 193, 'Regirock’s body is composed entirely of rocks. Recently&#44; a study made the startling discovery that the rocks were all unearthed from different locations.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (378, 'regice', 'ice', 'none', 0, 194, 'Regice cloaks itself with frigid air of -328 degrees Fahrenheit. Things will freeze solid just by going near this Pok&#233;mon. Its icy body is so cold&#44; it will not melt even if it is immersed in magma.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (379, 'registeel', 'steel', 'none', 0, 195, 'Registeel was imprisoned by people in ancient times. The metal composing its body is thought to be a curious substance that is not of this earth.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (380, 'latias', 'dragon', 'psychic', 0, 196, 'Latias is highly intelligent and capable of understanding human speech. It is covered with a glass-like down. The Pok&#233;mon enfolds its body with its down and refracts light to alter its appearance.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (381, 'latios', 'dragon', 'psychic', 0, 197, 'Latios will only open its heart to a Trainer with a compassionate spirit. This Pok&#233;mon can fly faster than a jet plane by folding its forelegs to minimize air resistance.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (382, 'kyogre', 'water', 'none', 0, 198, 'Kyogre is said to be the personification of the sea itself. Legends tell of its many clashes against Groudon&#44; as each sought to gain the power of nature.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (383, 'groudon', 'ground', 'none', 0, 199, 'Through Primal Reversion and with nature’s full power&#44; it will take back its true form. It can cause magma to erupt and expand the landmass of the world.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (384, 'rayquaza', 'dragon', 'flying', 0, 200, 'It flies forever through the ozone layer&#44; consuming meteoroids for sustenance. The many meteoroids in its body provide the energy it needs to Mega Evolve.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (385, 'jirachi', 'steel', 'psychic', 0, 201, 'Jirachi will awaken from its sleep of a thousand years if you sing to it in a voice of purity. It is said to make true any wish that people desire.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (386, 'deoxys-normal', 'psychic', 'none', 0, 202, 'Deoxys emerged from a virus that came from space. It is highly intelligent and wields psychokinetic powers. This Pok&#233;mon shoots lasers from the crystalline organ on its chest.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (387, 'turtwig', 'grass', 'none', 0, 203, 'It undertakes photosynthesis with its body&#44; making oxygen. The leaf on its head wilts if it is thirsty.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (388, 'grotle', 'grass', 'none', 387, 203, 'It knows where pure water wells up. It carries fellow Pok&#233;mon there on its back.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (389, 'torterra', 'grass', 'ground', 388, 203, 'Small Pok&#233;mon occasionally gather on its unmoving back to begin building their nests.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (390, 'chimchar', 'fire', 'none', 0, 204, 'The gas made in its belly burns from its rear end. The fire burns weakly when it feels sick.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (391, 'monferno', 'fire', 'fighting', 390, 204, 'It uses ceilings and walls to launch aerial attacks. Its fiery tail is but one weapon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (392, 'infernape', 'fire', 'fighting', 391, 204, 'It tosses its enemies around with agility. It uses all its limbs to fight in its own unique style.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (393, 'piplup', 'water', 'none', 0, 205, 'Because it is very proud&#44; it hates accepting food from people. Its thick down guards it from cold.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (394, 'prinplup', 'water', 'none', 393, 205, 'It lives a solitary life. Its wings deliver wicked blows that can snap even the thickest of trees.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (395, 'empoleon', 'water', 'steel', 394, 205, 'The three horns that extend from its beak attest to its power. The leader has the biggest horns.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (396, 'starly', 'normal', 'flying', 0, 206, 'They flock around mountains and fields&#44; chasing after bug Pok&#233;mon. Their singing is noisy and annoying.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (397, 'staravia', 'normal', 'flying', 396, 206, 'It lives in forests and fields. Squabbles over territory occur when flocks collide.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (398, 'staraptor', 'normal', 'flying', 397, 206, 'When Staravia evolve into Staraptor&#44; they leave the flock to live alone. They have sturdy wings.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (399, 'bidoof', 'normal', 'none', 0, 207, 'It constantly gnaws on logs and rocks to whittle down its front teeth. It nests alongside water.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (400, 'bibarel', 'normal', 'water', 399, 207, 'It makes its nest by damming streams with bark and mud. It is known as an industrious worker.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (401, 'kricketot', 'bug', 'none', 0, 208, 'When its antennae hit each other&#44; it sounds like the music of a xylophone.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (402, 'kricketune', 'bug', 'none', 401, 208, 'It signals its emotions with its melodies. Scientists are studying these melodic patterns.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (403, 'shinx', 'electric', 'none', 0, 209, 'All of its fur dazzles if danger is sensed. It flees while the foe is momentarily blinded.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (404, 'luxio', 'electric', 'none', 403, 209, 'Strong electricity courses through the tips of its sharp claws. A light scratch causes fainting in foes.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (405, 'luxray', 'electric', 'none', 404, 209, 'Luxray’s ability to see through objects comes in handy when it’s scouting for danger.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (406, 'budew', 'grass', 'poison', 0, 158, 'Over the winter&#44; it closes its bud and endures the cold. In spring&#44; the bud opens and releases pollen.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (407, 'roserade', 'grass', 'poison', 315, 158, 'With the movements of a dancer&#44; it strikes with whips that are densely lined with poison thorns.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (408, 'cranidos', 'rock', 'none', 0, 211, 'A lifelong jungle dweller from 100 million years ago&#44; it would snap obstructing trees with headbutts.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (409, 'rampardos', 'rock', 'none', 408, 211, 'Its skull is as hard as iron. It is a brute that tears down jungle trees while catching prey.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (410, 'shieldon', 'rock', 'steel', 0, 212, 'It was generated from a fossil dug out of a layer of clay that was older than anyone knows. It has a sturdy face.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (411, 'bastiodon', 'rock', 'steel', 410, 212, 'Any frontal attack is repulsed. It is a docile Pok&#233;mon that feeds on grass and berries.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (412, 'burmy', 'bug', 'none', 0, 213, 'If its cloak is broken in battle&#44; it quickly remakes the cloak with materials nearby.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (413, 'wormadam-plant', 'bug', 'grass', 412, 213, 'When Burmy evolved&#44; its cloak became a part of this Pok&#233;mon’s body. The cloak is never shed.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (414, 'mothim', 'bug', 'flying', 412, 213, 'It flutters around at night and steals honey from the Combee hive.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (415, 'combee', 'bug', 'flying', 0, 214, 'It collects and delivers honey to its colony. At night&#44; they cluster to form a beehive and sleep.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (416, 'vespiquen', 'bug', 'flying', 415, 214, 'Its abdomen is a honeycomb for grubs. It raises its grubs on honey collected by Combee.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (417, 'pachirisu', 'electric', 'none', 0, 215, 'A pair may be seen rubbing their cheek pouches together in an effort to share stored electricity.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (418, 'buizel', 'water', 'none', 0, 216, 'It inflates the flotation sac around its neck and pokes its head out of the water to see what is going on.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (419, 'floatzel', 'water', 'none', 418, 216, 'Its flotation sac developed as a result of pursuing aquatic prey. It can double as a rubber raft.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (420, 'cherubi', 'grass', 'none', 0, 217, 'It evolves by sucking the energy out of the small ball where it had been storing nutrients.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (421, 'cherrim', 'grass', 'none', 420, 217, 'If it senses strong sunlight&#44; it opens its folded petals to absorb the sun’s rays with its whole body.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (422, 'shellos', 'water', 'none', 0, 218, 'Its shape and coloration vary&#44; depending on its habitat.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (423, 'gastrodon', 'water', 'ground', 422, 218, 'It apparently had a huge shell for protection in ancient times. It lives in shallow tidal pools.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (424, 'ambipom', 'normal', 'none', 190, 93, 'To eat&#44; it deftly shucks nuts with its two tails. It rarely uses its arms now.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (425, 'drifloon', 'ghost', 'flying', 0, 219, 'These Pok&#233;mon are called the “Signpost for Wandering Spirits.” Children holding them sometimes vanish.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (426, 'drifblim', 'ghost', 'flying', 425, 219, 'It’s drowsy in daytime&#44; but flies off in the evening in big groups. No one knows where they go.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (427, 'buneary', 'normal', 'none', 0, 220, 'When it senses danger&#44; it perks up its ears. On cold nights&#44; it sleeps with its head tucked into its fur.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (428, 'lopunny', 'normal', 'none', 427, 220, 'The ears appear to be delicate. If they are touched roughly&#44; it kicks with its graceful legs.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (429, 'mismagius', 'ghost', 'none', 200, 98, 'Its cries sound like incantations to torment the foe. It appears where you least expect it.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (430, 'honchkrow', 'dark', 'flying', 198, 97, 'Becoming active at night&#44; it is known to swarm with numerous Murkrow in tow.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (431, 'glameow', 'normal', 'none', 0, 221, 'When it’s happy&#44; Glameow demonstrates beautiful movements of its tail&#44; like a dancing ribbon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (432, 'purugly', 'normal', 'none', 431, 221, 'To make itself appear intimidatingly beefy&#44; it tightly cinches its waist with its twin tails.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (433, 'chingling', 'psychic', 'none', 0, 184, 'There is an orb inside its mouth. When it hops&#44; the orb bounces all over and makes a ringing sound.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (434, 'stunky', 'poison', 'dark', 0, 223, 'It protects itself by spraying a noxious fluid from its rear. The stench lingers for 24 hours.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (435, 'skuntank', 'poison', 'dark', 434, 223, 'It sprays a stinky fluid from its tail. The fluid smells worse the longer it is allowed to fester.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (436, 'bronzor', 'steel', 'psychic', 0, 224, 'Implements shaped like it were discovered in ancient tombs. It is unknown if they are related.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (437, 'bronzong', 'steel', 'psychic', 436, 224, 'Ancient people believed that petitioning Bronzong for rain was the way to make crops grow.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (438, 'bonsly', 'rock', 'none', 0, 91, 'It prefers an arid atmosphere. It leaks water that looks like tears when adjusting its moisture level.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (439, 'mime-jr', 'psychic', 'fairy', 0, 57, 'It habitually mimics foes. Once mimicked&#44; the foe cannot take its eyes off this Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (440, 'happiny', 'normal', 'none', 0, 51, 'It carefully carries a round&#44; white rock that it thinks is an egg. It’s bothered by how curly its hair looks.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (441, 'chatot', 'normal', 'flying', 0, 228, 'It can learn and speak human words. If they gather&#44; they all learn the same saying.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (442, 'spiritomb', 'ghost', 'dark', 0, 229, 'It was bound to a fissure in an odd keystone as punishment for misdeeds 500 years ago.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (443, 'gible', 'dragon', 'ground', 0, 230, 'It nests in small&#44; horizontal holes in cave walls. It pounces to catch prey that stray too close.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (444, 'gabite', 'dragon', 'ground', 443, 230, 'As it digs to expand its nest&#44; it habitually digs up gems that it then hoards in its nest.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (445, 'garchomp', 'dragon', 'ground', 444, 230, 'It flies at speeds equal to a jet fighter plane. It never allows its prey to escape.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (446, 'munchlax', 'normal', 'none', 0, 72, 'It conceals food under the long fur on its body. It carts around this food stash and swallows it without chewing.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (447, 'riolu', 'fighting', 'none', 0, 232, 'The aura that emanates from its body intensifies to alert others if it is afraid or sad.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (448, 'lucario', 'fighting', 'steel', 447, 232, 'By reading the auras of all things&#44; it can tell how others are feeling from over half a mile away.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (449, 'hippopotas', 'ground', 'none', 0, 233, 'It enshrouds itself with sand to protect itself from germs. It does not enjoy getting wet.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (450, 'hippowdon', 'ground', 'none', 449, 233, 'It blasts internally stored sand from ports on its body to create a towering twister for attack.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (451, 'skorupi', 'poison', 'bug', 0, 234, 'It burrows under the sand to lie in wait for prey. Its tail claws can inject its prey with a savage poison.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (452, 'drapion', 'poison', 'dark', 451, 234, 'It has the power in its clawed arms to make scrap of a car. The tips of its claws release poison.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (453, 'croagunk', 'poison', 'fighting', 0, 235, 'Inflating its poison sacs&#44; it fills the area with an odd sound and hits flinching opponents with a poison jab.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (454, 'toxicroak', 'poison', 'fighting', 453, 235, 'Its knuckle claws secrete a toxin so vile that even a scratch could prove fatal.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (455, 'carnivine', 'grass', 'none', 0, 236, 'It binds itself to trees in marshes. It attracts prey with its sweet-smelling drool and gulps them down.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (456, 'finneon', 'water', 'none', 0, 237, 'After long exposure to sunlight&#44; the patterns on its tail fins shine vividly when darkness arrives.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (457, 'lumineon', 'water', 'none', 456, 237, 'To avoid detection by predators&#44; it crawls along the seafloor using the two fins on its chest.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (458, 'mantyke', 'water', 'flying', 0, 116, 'When it swims close to the surface of the ocean&#44; people aboard ships are able to observe the pattern on its back.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (459, 'snover', 'grass', 'ice', 0, 239, 'In the spring&#44; it grows berries with the texture of frozen treats around its belly.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (460, 'abomasnow', 'grass', 'ice', 459, 239, 'It lives a quiet life on mountains that are perpetually covered in snow. It hides itself by whipping up blizzards.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (461, 'weavile', 'dark', 'ice', 215, 109, 'They live in cold regions&#44; forming groups of four or five that hunt prey with impressive coordination.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (462, 'magnezone', 'electric', 'steel', 82, 34, 'It evolved from exposure to a special magnetic field. Three units generate magnetism.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (463, 'lickilicky', 'normal', 'none', 108, 48, 'Their saliva contains lots of components that can dissolve anything. The numbness caused by their lick does not dissipate.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (464, 'rhyperior', 'ground', 'rock', 112, 50, 'It puts rocks in holes in its palms and uses its muscles to shoot them. Geodude are shot at rare times.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (465, 'tangrowth', 'grass', 'none', 114, 52, 'Its vines grow so profusely that&#44; in the warm season&#44; you can’t even see its eyes.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (466, 'electivire', 'electric', 'none', 125, 60, 'It pushes the tips of its two tails against the foe&#44; then lets loose with over 20&#44;000 volts of power.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (467, 'magmortar', 'fire', 'none', 126, 61, 'It blasts fireballs of over 3&#44;600 degrees Fahrenheit out of its arms. Its breath also sears and sizzles.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (468, 'togekiss', 'fairy', 'flying', 176, 87, 'It shares many blessings with people who respect one another’s rights and avoid needless strife.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (469, 'yanmega', 'bug', 'flying', 193, 95, 'This six-legged Pok&#233;mon is easily capable of transporting an adult in flight. The wings on its tail help it stay balanced.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (470, 'leafeon', 'grass', 'none', 133, 67, 'Just like a plant&#44; it uses photosynthesis. As a result&#44; it is always enveloped in clear air.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (471, 'glaceon', 'ice', 'none', 133, 67, 'It lowers its body heat to freeze its fur. The hairs then become like needles it can fire.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (472, 'gliscor', 'ground', 'flying', 207, 104, 'Its flight is soundless. It uses its lengthy tail to carry off its prey... Then its elongated fangs do the rest.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (473, 'mamoswine', 'ice', 'ground', 221, 112, 'Its impressive tusks are made of ice. The population thinned when it turned warm after the ice age.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (474, 'porygon-z', 'normal', 'none', 233, 68, 'Its programming was modified to enable it to travel through alien dimensions. Seems there might have been an error...');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (475, 'gallade', 'psychic', 'fighting', 281, 140, 'A master of courtesy and swordsmanship&#44; it fights using extending swords on its elbows.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (476, 'probopass', 'rock', 'steel', 299, 147, 'It freely controls three small units called Mini-Noses using magnetic force.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (477, 'dusknoir', 'ghost', 'none', 356, 182, 'The antenna on its head captures radio waves from the world of spirits that command it to take people there.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (478, 'froslass', 'ice', 'ghost', 361, 186, 'Legends in snowy regions say that a woman who was lost on an icy mountain was reborn as Froslass.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (479, 'rotom', 'electric', 'ghost', 0, 240, 'Its body is composed of plasma. It is known to infiltrate electronic devices and wreak havoc.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (480, 'uxie', 'psychic', 'none', 0, 241, 'It is said that its emergence gave humans the intelligence to improve their quality of life.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (481, 'mesprit', 'psychic', 'none', 0, 242, 'It sleeps at the bottom of a lake. Its spirit is said to leave its body to fly on the lake’s surface.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (482, 'azelf', 'psychic', 'none', 0, 243, 'It is thought that Uxie&#44; Mesprit&#44; and Azelf all came from the same egg.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (483, 'dialga', 'steel', 'dragon', 0, 244, 'It has the power to control time. It appears in Sinnoh-region myths as an ancient deity.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (484, 'palkia', 'water', 'dragon', 0, 245, 'It has the ability to distort space. It is described as a deity in Sinnoh-region mythology.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (485, 'heatran', 'fire', 'steel', 0, 246, 'Boiling blood&#44; like magma&#44; circulates through its body. It makes its dwelling place in volcanic caves.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (486, 'regigigas', 'normal', 'none', 0, 247, 'There is an enduring legend that states this Pok&#233;mon towed continents with ropes.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (487, 'giratina-altered', 'ghost', 'dragon', 0, 248, 'It was banished for its violence. It silently gazed upon the old world from the Distortion World.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (488, 'cresselia', 'psychic', 'none', 0, 249, 'Those who sleep holding Cresselia’s feather are assured of joyful dreams. It is said to represent the crescent moon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (489, 'phione', 'water', 'none', 0, 250, 'It drifts in warm seas. It always returns to where it was born&#44; no matter how far it may have drifted.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (490, 'manaphy', 'water', 'none', 0, 250, 'It starts its life with a wondrous power that permits it to bond with any kind of Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (491, 'darkrai', 'dark', 'none', 0, 252, 'It can lull people to sleep and make them dream. It is active during nights of the new moon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (492, 'shaymin-land', 'grass', 'none', 0, 253, 'The blooming of Gracidea flowers confers the power of flight upon it. Feelings of gratitude are the message it delivers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (493, 'arceus', 'normal', 'none', 0, 254, 'It is told in mythology that this Pok&#233;mon was born before the universe even existed.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (494, 'victini', 'psychic', 'fire', 0, 255, 'When it shares the infinite energy it creates&#44; that being’s entire body will be overflowing with power.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (495, 'snivy', 'grass', 'none', 0, 256, 'They photosynthesize by bathing their tails in sunlight. When they are not feeling well&#44; their tails droop.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (496, 'servine', 'grass', 'none', 495, 256, 'When it gets dirty&#44; its leaves can’t be used in photosynthesis&#44; so it always keeps itself clean.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (497, 'serperior', 'grass', 'none', 496, 256, 'It can stop its opponents’ movements with just a glare. It takes in solar energy and boosts it internally.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (498, 'tepig', 'fire', 'none', 0, 257, 'It loves to eat roasted berries&#44; but sometimes it gets too excited and burns them to a crisp.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (499, 'pignite', 'fire', 'fighting', 498, 257, 'When its internal fire flares up&#44; its movements grow sharper and faster. When in trouble&#44; it emits smoke.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (500, 'emboar', 'fire', 'fighting', 499, 257, 'It has mastered fast and powerful fighting moves. It grows a beard of fire.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (501, 'oshawott', 'water', 'none', 0, 258, 'It fights using the scalchop on its stomach. In response to an attack&#44; it retaliates immediately by slashing.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (502, 'dewott', 'water', 'none', 501, 258, 'As a result of strict training&#44; each Dewott learns different forms for using the scalchops.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (503, 'samurott', 'water', 'none', 502, 258, 'One swing of the sword incorporated in its armor can fell an opponent. A simple glare from one of them quiets everybody.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (504, 'patrat', 'normal', 'none', 0, 259, 'Extremely cautious&#44; one of them will always be on the lookout&#44; but it won’t notice a foe coming from behind.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (505, 'watchog', 'normal', 'none', 504, 259, 'When they see an enemy&#44; their tails stand high&#44; and they spit the seeds of berries stored in their cheek pouches.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (506, 'lillipup', 'normal', 'none', 0, 260, 'Though it is a very brave Pok&#233;mon&#44; it’s also smart enough to check its foe’s strength and avoid battle.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (507, 'herdier', 'normal', 'none', 506, 260, 'It has black&#44; cape-like fur that is very hard and decreases the amount of damage it receives.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (508, 'stoutland', 'normal', 'none', 507, 260, 'Being wrapped in its long fur is so comfortable that a person would be fine even overnight on a wintry mountain.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (509, 'purrloin', 'dark', 'none', 0, 261, 'They steal from people for fun&#44; but their victims can’t help but forgive them. Their deceptively cute act is perfect.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (510, 'liepard', 'dark', 'none', 509, 261, 'Stealthily&#44; it sneaks up on its target&#44; striking from behind before its victim has a chance to react.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (511, 'pansage', 'grass', 'none', 0, 262, 'It’s good at finding berries and gathers them from all over. It’s kind enough to share them with friends.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (512, 'simisage', 'grass', 'none', 511, 262, 'Ill tempered&#44; it fights by swinging its barbed tail around wildly. The leaf growing on its head is very bitter.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (513, 'pansear', 'fire', 'none', 0, 263, 'This Pok&#233;mon lives in caves in volcanoes. The fire within the tuft on its head can reach 600 degrees Fahrenheit.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (514, 'simisear', 'fire', 'none', 513, 263, 'When it gets excited&#44; embers rise from its head and tail and it gets hot. For some reason&#44; it loves sweets.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (515, 'panpour', 'water', 'none', 0, 264, 'The water stored inside the tuft on its head is full of nutrients. Plants that receive its water grow large.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (516, 'simipour', 'water', 'none', 515, 264, 'It prefers places with clean water. When its tuft runs low&#44; it replenishes it by siphoning up water with its tail.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (517, 'munna', 'psychic', 'none', 0, 265, 'It eats the dreams of people and Pok&#233;mon. When it eats a pleasant dream&#44; it expels pink-colored mist.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (518, 'musharna', 'psychic', 'none', 517, 265, 'The dream mist coming from its forehead changes into many different colors depending on the dream that was eaten.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (519, 'pidove', 'normal', 'flying', 0, 266, 'These Pok&#233;mon live in cities. They are accustomed to people. Flocks often gather in parks and plazas.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (520, 'tranquill', 'normal', 'flying', 519, 266, 'No matter where in the world it goes&#44; it knows where its nest is&#44; so it never gets separated from its Trainer.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (521, 'unfezant', 'normal', 'flying', 520, 266, 'Males have plumage on their heads. They will never let themselves feel close to anyone other than their Trainers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (522, 'blitzle', 'electric', 'none', 0, 267, 'Its mane shines when it discharges electricity. They use the frequency and rhythm of these flashes to communicate.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (523, 'zebstrika', 'electric', 'none', 522, 267, 'They have lightning-like movements. When Zebstrika run at full speed&#44; the sound of thunder reverberates.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (524, 'roggenrola', 'rock', 'none', 0, 268, 'They were discovered a hundred years ago in an earthquake fissure. Inside each one is an energy core.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (525, 'boldore', 'rock', 'none', 524, 268, 'When it is healthy&#44; its core sticks out. Always facing the same way&#44; it swiftly moves front to back and left to right.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (526, 'gigalith', 'rock', 'none', 525, 268, 'Compressing the energy from its internal core lets it fire off an attack capable of blowing away a mountain.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (527, 'woobat', 'psychic', 'flying', 0, 269, 'The heart-shaped mark left on a body after a Woobat has been attached to it is said to bring good fortune.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (528, 'swoobat', 'psychic', 'flying', 527, 269, 'Anyone who comes into contact with the ultrasonic waves emitted by a courting male experiences a positive mood shift.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (529, 'drilbur', 'ground', 'none', 0, 270, 'By spinning its body&#44; it can dig straight through the ground at a speed of 30 mph.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (530, 'excadrill', 'ground', 'steel', 529, 270, 'More than 300 feet below the surface&#44; they build mazelike nests. Their activity can be destructive to subway tunnels.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (531, 'audino', 'normal', 'none', 0, 271, 'It touches others with the feelers on its ears&#44; using the sound of their heartbeats to tell how they are feeling.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (532, 'timburr', 'fighting', 'none', 0, 272, 'Always carrying squared logs&#44; they help out with construction. As they grow&#44; they carry bigger logs.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (533, 'gurdurr', 'fighting', 'none', 532, 272, 'This Pok&#233;mon is so muscular and strongly built that even a group of wrestlers could not make it budge an inch.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (534, 'conkeldurr', 'fighting', 'none', 533, 272, 'Rather than rely on force&#44; they master moves that utilize the centrifugal force of spinning concrete.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (535, 'tympole', 'water', 'none', 0, 273, 'By vibrating its cheeks&#44; it emits sound waves imperceptible to humans. It uses the rhythm of these sounds to talk.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (536, 'palpitoad', 'water', 'ground', 535, 273, 'It lives in the water and on land. It uses its long&#44; sticky tongue to immobilize its opponents.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (537, 'seismitoad', 'water', 'ground', 536, 273, 'They shoot paralyzing liquid from their head bumps. They use vibration to hurt their opponents.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (538, 'throh', 'fighting', 'none', 0, 274, 'When it encounters a foe bigger than itself&#44; it wants to throw it. It changes belts as it gets stronger.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (539, 'sawk', 'fighting', 'none', 0, 275, 'Tying their belts gets them pumped and makes their punches more destructive. Disturbing their training angers them.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (540, 'sewaddle', 'bug', 'grass', 0, 276, 'Since this Pok&#233;mon makes its own clothes out of leaves&#44; it is a popular mascot for fashion designers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (541, 'swadloon', 'bug', 'grass', 540, 276, 'It protects itself from the cold by wrapping up in leaves. It stays on the move&#44; eating leaves in forests.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (542, 'leavanny', 'bug', 'grass', 541, 276, 'It keeps its eggs warm with heat from fermenting leaves. It also uses leaves to make warm wrappings for Sewaddle.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (543, 'venipede', 'bug', 'poison', 0, 277, 'Its bite injects a potent poison&#44; enough to paralyze large bird Pok&#233;mon that try to prey on it.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (544, 'whirlipede', 'bug', 'poison', 543, 277, 'It is usually motionless&#44; but when attacked&#44; it rotates at high speed and then crashes into its opponent.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (545, 'scolipede', 'bug', 'poison', 544, 277, 'With quick movements&#44; it chases down its foes&#44; attacking relentlessly with its horns until it prevails.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (546, 'cottonee', 'grass', 'fairy', 0, 278, 'Perhaps because they feel more at ease in a group&#44; they stick to others they find. They end up looking like a cloud.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (547, 'whimsicott', 'grass', 'fairy', 546, 278, 'Like the wind&#44; it can slip through any gap&#44; no matter how small. It leaves balls of white fluff behind.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (548, 'petilil', 'grass', 'none', 0, 279, 'Since they prefer moist&#44; nutrient-rich soil&#44; the areas where Petilil live are known to be good for growing plants.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (549, 'lilligant', 'grass', 'none', 548, 279, 'Even veteran Trainers face a challenge in getting its beautiful flower to bloom. This Pok&#233;mon is popular with celebrities.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (550, 'basculin-red-striped', 'water', 'none', 0, 280, 'Red and blue Basculin usually do not get along&#44; but sometimes members of one school mingle with the other’s school.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (551, 'sandile', 'ground', 'dark', 0, 281, 'They live buried in the sands of the desert. The sun-warmed sands prevent their body temperature from dropping.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (552, 'krokorok', 'ground', 'dark', 551, 281, 'The special membrane covering its eyes can sense the heat of objects&#44; so it can see its surroundings even in darkness.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (553, 'krookodile', 'ground', 'dark', 552, 281, 'They never allow prey to escape. Their jaws are so powerful&#44; they can crush the body of an automobile.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (554, 'darumaka', 'fire', 'none', 0, 282, 'When it sleeps&#44; it pulls its limbs into its body and its internal fire goes down to 1&#44;100 degrees Fahrenheit.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (555, 'darmanitan-standard', 'fire', 'none', 554, 282, 'Its internal fire burns at 2&#44;500 degrees Fahrenheit&#44; making enough power that it can destroy a dump truck with one punch.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (556, 'maractus', 'grass', 'none', 0, 283, 'Arid regions are their habitat. They move rhythmically&#44; making a sound similar to maracas.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (557, 'dwebble', 'bug', 'rock', 0, 284, 'When it finds a stone of a suitable size&#44; it secretes a liquid from its mouth to open up a hole to crawl into.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (558, 'crustle', 'bug', 'rock', 557, 284, 'Competing for territory&#44; Crustle fight viciously. The one whose boulder is broken is the loser of the battle.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (559, 'scraggy', 'dark', 'fighting', 0, 285, 'Proud of its sturdy skull&#44; it suddenly headbutts everything&#44; but its weight makes it unstable&#44; too.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (560, 'scrafty', 'dark', 'fighting', 559, 285, 'It can smash concrete blocks with its kicking attacks. The one with the biggest crest is the group leader.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (561, 'sigilyph', 'psychic', 'flying', 0, 286, 'The guardians of an ancient city&#44; they always fly the same route while keeping watch for invaders.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (562, 'yamask', 'ghost', 'none', 0, 287, 'Each of them carries a mask that used to be its face when it was human. Sometimes they look at it and cry.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (563, 'cofagrigus', 'ghost', 'none', 562, 287, 'Grave robbers who mistake them for real coffins and get too close end up trapped inside their bodies.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (564, 'tirtouga', 'water', 'rock', 0, 288, 'Restored from a fossil&#44; this Pok&#233;mon can dive to depths beyond half a mile.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (565, 'carracosta', 'water', 'rock', 564, 288, 'It could knock out a foe with a slap from one of its developed front appendages and chew it up&#44; shell or bones and all.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (566, 'archen', 'rock', 'flying', 0, 289, 'Said to be an ancestor of bird Pok&#233;mon&#44; they were unable to fly and moved about by hopping from one branch to another.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (567, 'archeops', 'rock', 'flying', 566, 289, 'It runs better than it flies. It takes off into the sky by running at a speed of 25 mph.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (568, 'trubbish', 'poison', 'none', 0, 290, 'Inhaling the gas they belch will make you sleep for a week. They prefer unsanitary places.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (569, 'garbodor', 'poison', 'none', 568, 290, 'Consuming garbage makes new kinds of poison gases and liquids inside their bodies.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (570, 'zorua', 'dark', 'none', 0, 291, 'To protect themselves from danger&#44; they hide their true identities by transforming into people and Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (571, 'zoroark', 'dark', 'none', 570, 291, 'Bonds between these Pok&#233;mon are very strong. It protects the safety of its pack by tricking its opponents.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (572, 'minccino', 'normal', 'none', 0, 292, 'These Pok&#233;mon prefer a tidy habitat. They are always sweeping and dusting&#44; using their tails as brooms.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (573, 'cinccino', 'normal', 'none', 572, 292, 'Cinccino’s body is coated in a special oil that helps it deflect attacks&#44; such as punches.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (574, 'gothita', 'psychic', 'none', 0, 293, 'They intently observe both Trainers and Pok&#233;mon. Apparently&#44; they are looking at something that only Gothita can see.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (575, 'gothorita', 'psychic', 'none', 574, 293, 'According to many old tales&#44; it creates friends for itself by controlling sleeping children on starry nights.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (576, 'gothitelle', 'psychic', 'none', 575, 293, 'They can predict the future from the placement and movement of the stars. They can see Trainers’ life spans.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (577, 'solosis', 'psychic', 'none', 0, 294, 'They drive away attackers by unleashing psychic power. They can use telepathy to talk with others.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (578, 'duosion', 'psychic', 'none', 577, 294, 'When their two divided brains think the same thoughts&#44; their psychic power is maximized.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (579, 'reuniclus', 'psychic', 'none', 578, 294, 'When Reuniclus shake hands&#44; a network forms between their brains&#44; increasing their psychic power.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (580, 'ducklett', 'water', 'flying', 0, 295, 'They are better at swimming than flying&#44; and they happily eat their favorite food&#44; peat moss&#44; as they dive underwater.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (581, 'swanna', 'water', 'flying', 580, 295, 'Swanna start to dance at dusk. The one dancing in the middle is the leader of the flock.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (582, 'vanillite', 'ice', 'none', 0, 296, 'This Pok&#233;mon formed from icicles bathed in energy from the morning sun. It sleeps buried in snow.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (583, 'vanillish', 'ice', 'none', 582, 296, 'Snowy mountains are this Pok&#233;mon’s habitat. During an ancient ice age&#44; they moved to southern areas.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (584, 'vanilluxe', 'ice', 'none', 583, 296, 'Swallowing large amounts of water&#44; they make snow clouds inside their bodies and&#44; when angry&#44; cause violent blizzards.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (585, 'deerling', 'normal', 'grass', 0, 297, 'The turning of the seasons changes the color and scent of this Pok&#233;mon’s fur. People use it to mark the seasons.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (586, 'sawsbuck', 'normal', 'grass', 585, 297, 'They migrate according to the seasons&#44; so some people call Sawsbuck the harbingers of spring.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (587, 'emolga', 'electric', 'flying', 0, 298, 'The energy made in its cheeks’ electric pouches is stored inside its membrane and released while it is gliding.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (588, 'karrablast', 'bug', 'none', 0, 299, 'For some reason they evolve when they receive electrical energy while they are attacking Shelmet.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (589, 'escavalier', 'bug', 'steel', 588, 299, 'These Pok&#233;mon evolve by wearing the shell covering of a Shelmet. The steel armor protects their whole body.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (590, 'foongus', 'grass', 'poison', 0, 300, 'It lures Pok&#233;mon with its pattern that looks just like a Pok&#233; Ball&#44; then releases poison spores.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (591, 'amoonguss', 'grass', 'poison', 590, 300, 'It lures prey close by dancing and waving its arm caps&#44; which resemble Pok&#233; Balls&#44; in a swaying motion.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (592, 'frillish', 'water', 'ghost', 0, 301, 'If its veil-like arms stun and wrap a foe&#44; that foe will be dragged miles below the surface&#44; never to return.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (593, 'jellicent', 'water', 'ghost', 592, 301, 'The fate of the ships and crew that wander into Jellicent’s habitat: all sunken&#44; all lost&#44; all vanished.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (594, 'alomomola', 'water', 'none', 0, 302, 'It gently holds injured and weak Pok&#233;mon in its fins. Its special membrane heals their wounds.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (595, 'joltik', 'bug', 'electric', 0, 303, 'They attach themselves to large-bodied Pok&#233;mon and absorb static electricity&#44; which they store in an electric pouch.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (596, 'galvantula', 'bug', 'electric', 595, 303, 'When attacked&#44; they create an electric barrier by spitting out many electrically charged threads.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (597, 'ferroseed', 'grass', 'steel', 0, 304, 'It absorbs the iron it finds in the rock while clinging to the ceiling. It shoots spikes when in danger.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (598, 'ferrothorn', 'grass', 'steel', 597, 304, 'They attach themselves to cave ceilings&#44; firing steel spikes at targets passing beneath them.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (599, 'klink', 'steel', 'none', 0, 305, 'The two minigears that mesh together are predetermined. Each will rebound from other minigears without meshing.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (600, 'klang', 'steel', 'none', 599, 305, 'A minigear and big gear comprise its body. If the minigear it launches at a foe doesn’t return&#44; it will die.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (601, 'klinklang', 'steel', 'none', 600, 305, 'Its red core functions as an energy tank. It fires the charged energy through its spikes into an area.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (602, 'tynamo', 'electric', 'none', 0, 306, 'One alone can emit only a trickle of electricity&#44; so a group of them gathers to unleash a powerful electric shock.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (603, 'eelektrik', 'electric', 'none', 602, 306, 'These Pok&#233;mon have a big appetite. When they spot their prey&#44; they attack it and paralyze it with electricity.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (604, 'eelektross', 'electric', 'none', 603, 306, 'They crawl out of the ocean using their arms. They will attack prey on shore and immediately drag it into the ocean.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (605, 'elgyem', 'psychic', 'none', 0, 307, 'Rumors of its origin are linked to a UFO crash site in the desert 50 years ago.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (606, 'beheeyem', 'psychic', 'none', 605, 307, 'It uses psychic power to control an opponent’s brain and tamper with its memories.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (607, 'litwick', 'ghost', 'fire', 0, 308, 'Litwick shines a light that absorbs the life energy of people and Pok&#233;mon&#44; which becomes the fuel that it burns.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (608, 'lampent', 'ghost', 'fire', 607, 308, 'It arrives near the moment of death and steals spirit from the body.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (609, 'chandelure', 'ghost', 'fire', 608, 308, 'The spirits burned up in its ominous flame lose their way and wander this world forever.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (610, 'axew', 'dragon', 'none', 0, 309, 'They mark their territory by leaving gashes in trees with their tusks. If a tusk breaks&#44; a new one grows in quickly.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (611, 'fraxure', 'dragon', 'none', 610, 309, 'A broken tusk will not grow back&#44; so it diligently sharpens its tusks on river rocks after the end of a battle.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (612, 'haxorus', 'dragon', 'none', 611, 309, 'Their sturdy tusks will stay sharp even if used to cut steel beams. These Pok&#233;mon are covered in hard armor.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (613, 'cubchoo', 'ice', 'none', 0, 310, 'Their snot is a barometer of health. When healthy&#44; their snot is sticky and the power of their ice moves increases.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (614, 'beartic', 'ice', 'none', 613, 310, 'It freezes its breath to create fangs and claws of ice to fight with. Cold northern areas are its habitat.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (615, 'cryogonal', 'ice', 'none', 0, 311, 'They are composed of ice crystals. They capture prey with chains of ice&#44; freezing the prey at -148 degrees Fahrenheit.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (616, 'shelmet', 'bug', 'none', 0, 312, 'It evolves when bathed in an electric-like energy along with Karrablast. The reason is still unknown.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (617, 'accelgor', 'bug', 'none', 616, 312, 'When its body dries out&#44; it weakens. So&#44; to prevent dehydration&#44; it wraps itself in many layers of thin membrane.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (618, 'stunfisk', 'ground', 'electric', 0, 313, 'It conceals itself in the mud of the seashore. Then it waits. When prey touch it&#44; it delivers a jolt of electricity.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (619, 'mienfoo', 'fighting', 'none', 0, 314, 'In fights&#44; they dominate with onslaughts of flowing&#44; continuous attacks. With their sharp claws&#44; they cut enemies.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (620, 'mienshao', 'fighting', 'none', 619, 314, 'Using the long fur on its arms like whips&#44; it launches into combo attacks that&#44; once started&#44; no one can stop.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (621, 'druddigon', 'dragon', 'none', 0, 315, 'It warms its body by absorbing sunlight with its wings. When its body temperature falls&#44; it can no longer move.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (622, 'golett', 'ground', 'ghost', 0, 316, 'Ancient science fashioned this Pok&#233;mon from clay. It’s been active for thousands of years.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (623, 'golurk', 'ground', 'ghost', 622, 316, 'It flies across the sky at Mach speeds. Removing the seal on its chest makes its internal energy go out of control.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (624, 'pawniard', 'dark', 'steel', 0, 317, 'Ignoring their injuries&#44; groups attack by sinking the blades that cover their bodies into their prey.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (625, 'bisharp', 'dark', 'steel', 624, 317, 'Bisharp pursues prey in the company of a large group of Pawniard. Then Bisharp finishes off the prey.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (626, 'bouffalant', 'normal', 'none', 0, 318, 'Their fluffy fur absorbs damage&#44; even if they strike foes with a fierce headbutt.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (627, 'rufflet', 'normal', 'flying', 0, 319, 'They will challenge anything&#44; even strong opponents&#44; without fear. Their frequent fights help them become stronger.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (628, 'braviary', 'normal', 'flying', 627, 319, 'They fight for their friends without any thought about danger to themselves. One can carry a car while flying.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (629, 'vullaby', 'dark', 'flying', 0, 320, 'Their wings are too tiny to allow them to fly. They guard their posteriors with bones that were gathered by Mandibuzz.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (630, 'mandibuzz', 'dark', 'flying', 629, 320, 'Watching from the sky&#44; they swoop to strike weakened Pok&#233;mon on the ground. They decorate themselves with bones.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (631, 'heatmor', 'fire', 'none', 0, 321, 'It draws in air through its tail&#44; transforms it into fire&#44; and uses it like a tongue. It melts Durant and eats them.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (632, 'durant', 'bug', 'steel', 0, 322, 'They attack in groups&#44; covering themselves in steel armor to protect themselves from Heatmor.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (633, 'deino', 'dark', 'dragon', 0, 323, 'Lacking sight&#44; it’s unaware of its surroundings&#44; so it bumps into things and eats anything that moves.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (634, 'zweilous', 'dark', 'dragon', 633, 323, 'After it has eaten up all the food in its territory&#44; it moves to another area. Its two heads do not get along.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (635, 'hydreigon', 'dark', 'dragon', 634, 323, 'It responds to movement by attacking. This scary&#44; three-headed Pok&#233;mon devours everything in its path!');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (636, 'larvesta', 'bug', 'fire', 0, 324, 'The base of volcanoes is where they make their homes. They shoot fire from their five horns to repel attacking enemies.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (637, 'volcarona', 'bug', 'fire', 636, 324, 'When volcanic ash darkened the atmosphere&#44; it is said that Volcarona’s fire provided a replacement for the sun.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (638, 'cobalion', 'steel', 'fighting', 0, 325, 'It has a body and heart of steel. It worked with its allies to punish people when they hurt Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (639, 'terrakion', 'rock', 'fighting', 0, 326, 'Spoken of in legend&#44; this Pok&#233;mon used its phenomenal power to destroy a castle in its effort to protect Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (640, 'virizion', 'grass', 'fighting', 0, 327, 'Legends say this Pok&#233;mon confounded opponents with its swift movements.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (641, 'tornadus-incarnate', 'flying', 'none', 0, 328, 'Tornadus expels massive energy from its tail&#44; causing severe storms. Its power is great enough to blow houses away.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (642, 'thundurus-incarnate', 'electric', 'flying', 0, 329, 'As it flies around&#44; it shoots lightning all over the place and causes forest fires. It is therefore disliked.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (643, 'reshiram', 'dragon', 'fire', 0, 330, 'When Reshiram’s tail flares&#44; the heat energy moves the atmosphere and changes the world’s weather.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (644, 'zekrom', 'dragon', 'electric', 0, 331, 'Concealing itself in lightning clouds&#44; it flies throughout the Unova region. It creates electricity in its tail.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (645, 'landorus-incarnate', 'ground', 'flying', 0, 332, 'From the forces of lightning and wind&#44; it creates energy to give nutrients to the soil and make the land abundant.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (646, 'kyurem', 'dragon', 'ice', 0, 333, 'It generates a powerful&#44; freezing energy inside itself&#44; but its body became frozen when the energy leaked out.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (647, 'keldeo-ordinary', 'water', 'fighting', 0, 334, 'When it is resolute&#44; its body fills with power and it becomes swifter. Its jumps are then too fast to follow.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (648, 'meloetta-aria', 'normal', 'psychic', 0, 335, 'Its melodies are sung with a special vocalization method that can control the feelings of those who hear it.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (649, 'genesect', 'bug', 'steel', 0, 336, 'This Pok&#233;mon existed 300 million years ago. Team Plasma altered it and attached a cannon to its back.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (650, 'chespin', 'grass', 'none', 0, 337, 'Such a thick shell of wood covers its head and back that even a direct hit from a truck wouldn’t faze it.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (651, 'quilladin', 'grass', 'none', 650, 337, 'They strengthen their lower bodies by running into one another. They are very kind and won’t start fights.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (652, 'chesnaught', 'grass', 'fighting', 651, 337, 'When it takes a defensive posture with its fists guarding its face&#44; it could withstand a bomb blast.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (653, 'fennekin', 'fire', 'none', 0, 338, 'As it walks&#44; it munches on a twig in place of a snack. It intimidates opponents by puffing hot air out of its ears.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (654, 'braixen', 'fire', 'none', 653, 338, 'When the twig is plucked from its tail&#44; friction sets the twig alight. The flame is used to send signals to its allies.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (655, 'delphox', 'fire', 'psychic', 654, 338, 'Using psychic power&#44; it generates a fiery vortex of 5&#44;400 degrees Fahrenheit&#44; incinerating foes swept into this whirl of flame.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (656, 'froakie', 'water', 'none', 0, 339, 'It protects its skin by covering its body in delicate bubbles. Beneath its happy-go-lucky air&#44; it keeps a watchful eye on its surroundings.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (657, 'frogadier', 'water', 'none', 656, 339, 'Its swiftness is unparalleled. It can scale a tower of more than 2&#44;000 feet in a minute’s time.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (658, 'greninja', 'water', 'dark', 657, 339, 'It appears and vanishes with a ninja’s grace. It toys with its enemies using swift movements&#44; while slicing them with throwing stars of sharpest water.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (659, 'bunnelby', 'normal', 'none', 0, 340, 'It has ears like shovels. Digging holes strengthens its ears so much that they can sever thick roots effortlessly.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (660, 'diggersby', 'normal', 'ground', 659, 340, 'As powerful as an excavator&#44; its ears can reduce dense bedrock to rubble. When it’s finished digging&#44; it lounges lazily.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (661, 'fletchling', 'normal', 'flying', 0, 341, 'Despite the beauty of its lilting voice&#44; it’s merciless to intruders that enter its territory.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (662, 'fletchinder', 'fire', 'flying', 661, 341, 'The hotter the flame sac on its belly&#44; the faster it can fly&#44; but it takes some time to get the fire going.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (663, 'talonflame', 'fire', 'flying', 662, 341, 'When attacking prey&#44; it can reach speeds of up to 310 mph. It finishes its prey off with a colossal kick.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (664, 'scatterbug', 'bug', 'none', 0, 342, 'The powder that covers its body regulates its temperature&#44; so it can live in any region or climate.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (665, 'spewpa', 'bug', 'none', 664, 342, 'The beaks of bird Pok&#233;mon can’t begin to scratch its stalwart body. To defend itself&#44; it spews powder.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (666, 'vivillon', 'bug', 'flying', 665, 342, 'The patterns on this Pok&#233;mon’s wings depend on the climate and topography of its habitat. It scatters colorful scales.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (667, 'litleo', 'fire', 'normal', 0, 343, 'They set off on their own from their pride and live by themselves to become stronger. These hot-blooded Pok&#233;mon are quick to fight.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (668, 'pyroar', 'fire', 'normal', 667, 343, 'With fiery breath of more than 10&#44;000 degrees Fahrenheit&#44; they viciously threaten any challenger. The females protect the pride’s cubs.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (669, 'flabebe', 'fairy', 'none', 0, 344, 'When it finds a flower it likes&#44; it dwells on that flower its whole life long. It floats in the wind’s embrace with an untroubled heart.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (670, 'floette', 'fairy', 'none', 669, 344, 'When the flowers of a well-tended flower bed bloom&#44; it appears and celebrates with an elegant dance.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (671, 'florges', 'fairy', 'none', 670, 344, 'In times long past&#44; governors of castles would invite Florges to create flower gardens to embellish the castle domains.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (672, 'skiddo', 'grass', 'none', 0, 345, 'If it has sunshine and water&#44; it doesn’t need to eat&#44; because it can generate energy from the leaves on its back.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (673, 'gogoat', 'grass', 'none', 672, 345, 'They inhabit mountainous regions. The leader of the herd is decided by a battle of clashing horns.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (674, 'pancham', 'fighting', 'none', 0, 346, 'It does its level best to glare and pull a scary face&#44; but it can’t help grinning if anyone pats its head.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (675, 'pangoro', 'fighting', 'dark', 674, 346, 'It charges ahead and bashes its opponents like a berserker&#44; uncaring about any hits it might take. Its arms are mighty enough to snap a telephone pole.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (676, 'furfrou', 'normal', 'none', 0, 347, 'Historically&#44; in the Kalos region&#44; these Pok&#233;mon were the designated guardians of the king.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (677, 'espurr', 'psychic', 'none', 0, 348, 'It has enough psychic energy to blast everything within 300 feet of itself&#44; but it has no control over its power.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (678, 'meowstic-male', 'psychic', 'none', 677, 348, 'The eyeball patterns on the interior of its ears emit psychic energy. It keeps the patterns tightly covered because that power is too immense.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (679, 'honedge', 'steel', 'ghost', 0, 349, 'If anyone dares to grab its hilt&#44; it wraps a blue cloth around that person’s arm and drains that person’s life energy completely.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (680, 'doublade', 'steel', 'ghost', 679, 349, 'The complex attack patterns of its two swords are unstoppable&#44; even for an opponent greatly accomplished at swordplay.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (681, 'aegislash-shield', 'steel', 'ghost', 680, 349, 'Apparently&#44; it can detect the innate qualities of leadership. According to legend&#44; whoever it recognizes is destined to become king.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (682, 'spritzee', 'fairy', 'none', 0, 350, 'In the past&#44; rather than using perfume&#44; royal ladies carried a Spritzee that would waft a fragrance they liked.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (683, 'aromatisse', 'fairy', 'none', 682, 350, 'Its scent is so overpowering that&#44; unless a Trainer happens to really enjoy the smell&#44; he or she will have a hard time walking alongside it.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (684, 'swirlix', 'fairy', 'none', 0, 351, 'Because it eats nothing but sweets&#44; its fur is as sticky sweet as cotton candy.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (685, 'slurpuff', 'fairy', 'none', 684, 351, 'Its sense of smell is 100 million times better than a human’s&#44; so even the faintest scent tells it about everything in the area. It’s like it can see with its nose!');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (686, 'inkay', 'dark', 'psychic', 0, 352, 'It flashes the light-emitting spots on its body&#44; which drains its opponent’s will to fight. It takes the opportunity to scuttle away and hide.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (687, 'malamar', 'dark', 'psychic', 686, 352, 'It lures its prey close with hypnotic motions&#44; then wraps its tentacles around it before finishing it off with digestive fluids.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (688, 'binacle', 'rock', 'water', 0, 353, 'They stretch and then contract&#44; yanking their rocks along with them in bold hops. They eat seaweed that washes up on the shoreline.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (689, 'barbaracle', 'rock', 'water', 688, 353, 'Barbaracle’s legs and hands have minds of their own&#44; and they will move independently. But they usually follow the head’s orders.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (690, 'skrelp', 'poison', 'water', 0, 354, 'It looks just like rotten kelp. It hides from foes while storing up power for its evolution.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (691, 'dragalge', 'poison', 'dragon', 690, 354, 'Tales are told of ships that wander into seas where Dragalge live&#44; never to return.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (692, 'clauncher', 'water', 'none', 0, 355, 'Through controlled explosions of internal gas&#44; it can expel water like a pistol shot. At close distances&#44; it can shatter rock.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (693, 'clawitzer', 'water', 'none', 692, 355, 'By expelling water from the nozzle in the back of its claw&#44; it can move at a speed of 60 knots.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (694, 'helioptile', 'electric', 'normal', 0, 356, 'The frills on either side of its head have cells that generate electricity when exposed to sunlight.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (695, 'heliolisk', 'electric', 'normal', 694, 356, 'It stimulates its muscles with electricity&#44; boosting the strength in its legs and enabling it to run 100 yards in five seconds.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (696, 'tyrunt', 'rock', 'dragon', 0, 357, 'Its immense jaws have enough destructive force that it can chew up an automobile. It lived 100 million years ago.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (697, 'tyrantrum', 'rock', 'dragon', 696, 357, 'Nothing could stop this Pok&#233;mon 100 million years ago&#44; so it behaved like a king.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (698, 'amaura', 'rock', 'ice', 0, 358, 'This calm Pok&#233;mon lived in a cold land where there were no violent predators like Tyrantrum.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (699, 'aurorus', 'rock', 'ice', 698, 358, 'Using its diamond-shaped crystals&#44; it can instantly create a wall of ice to block an opponent’s attack.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (700, 'sylveon', 'fairy', 'none', 133, 67, 'It wraps its ribbonlike feelers around the arm of its beloved Trainer and walks with him or her.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (701, 'hawlucha', 'fighting', 'flying', 0, 359, 'With its wings&#44; it controls its position in the air. It likes to attack from above&#44; a maneuver that is difficult to defend against.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (702, 'dedenne', 'electric', 'fairy', 0, 360, 'It uses its tail to absorb electricity from power plants or from outlets in houses&#44; and then it fires the electricity from its whiskers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (703, 'carbink', 'rock', 'fairy', 0, 361, 'It has slept underground for hundreds of millions of years since its birth. It’s occasionally found during the excavation of caves.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (704, 'goomy', 'dragon', 'none', 0, 362, 'It’s covered in a slimy membrane that makes any punches or kicks slide off it harmlessly.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (705, 'sliggoo', 'dragon', 'none', 704, 362, 'Its four horns are a high-performance radar system. It uses them to sense sounds and smells&#44; rather than using ears or a nose.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (706, 'goodra', 'dragon', 'none', 705, 362, 'It attacks with retractable horns. It throws a punch that’s the equivalent of the force of a hundred pro boxers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (707, 'klefki', 'steel', 'fairy', 0, 363, 'It never lets go of a key that it likes&#44; so people give it the keys to vaults and safes as a way to prevent crime.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (708, 'phantump', 'ghost', 'grass', 0, 364, 'According to old tales&#44; these Pok&#233;mon are stumps possessed by the spirits of children who died while lost in the forest.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (709, 'trevenant', 'ghost', 'grass', 708, 364, 'Using its roots as a nervous system&#44; it controls the trees in the forest. It’s kind to the Pok&#233;mon that reside in its body.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (710, 'pumpkaboo-average', 'ghost', 'grass', 0, 365, 'It is said to carry wandering spirits to the place where they belong so they can move on.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (711, 'gourgeist-average', 'ghost', 'grass', 710, 365, 'It enwraps its prey in its hairlike arms. It sings joyfully as it observes the suffering of its prey.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (712, 'bergmite', 'ice', 'none', 0, 366, 'Using air of -150 degrees Fahrenheit&#44; they freeze opponents solid. They live in herds above the snow line on mountains.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (713, 'avalugg', 'ice', 'none', 712, 366, 'The way several Bergmite huddle on its back makes it look like an aircraft carrier made of ice.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (714, 'noibat', 'flying', 'dragon', 0, 367, 'Even a robust wrestler will become dizzy and unable to stand when exposed to its 200&#44;000-hertz ultrasonic waves.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (715, 'noivern', 'flying', 'dragon', 714, 367, 'The ultrasonic waves it emits from its ears can reduce a large boulder to pebbles. It swoops out of the dark to attack.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (716, 'xerneas', 'fairy', 'none', 0, 368, 'When the horns on its head shine in seven colors&#44; it is said to be sharing everlasting life.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (717, 'yveltal', 'dark', 'flying', 0, 369, 'When its life comes to an end&#44; it absorbs the life energy of every living thing and turns into a cocoon once more.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (718, 'zygarde', 'dragon', 'ground', 0, 370, 'It’s hypothesized that it’s monitoring those who destroy the ecosystem from deep in the cave where it lives.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (719, 'diancie', 'rock', 'fairy', 0, 371, 'It can instantly create many diamonds by compressing the carbon in the air between its hands.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (720, 'hoopa', 'psychic', 'ghost', 0, 372, 'It is said to be able to seize anything it desires with its six rings and six huge arms. With its power sealed&#44; it is transformed into a much smaller form.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (721, 'volcanion', 'fire', 'water', 0, 373, 'It expels its internal steam from the arms on its back. It has enough power to blow away a mountain.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (722, 'rowlet', 'grass', 'flying', 0, 374, 'Silently it glides&#44; drawing near its targets. Before they even notice it&#44; it begins to pelt them with vicious kicks.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (723, 'dartrix', 'grass', 'flying', 722, 374, 'It throws sharp feathers called blade quills at enemies or prey. It seldom misses.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (724, 'decidueye', 'grass', 'ghost', 723, 374, 'Although basically cool and cautious&#44; when it’s caught by surprise&#44; it’s seized by panic.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (725, 'litten', 'fire', 'none', 0, 375, 'It doesn’t allow its emotions to be easily seen. Earning its trust takes time. It prefers solitude.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (726, 'torracat', 'fire', 'none', 725, 375, 'It boasts powerful front legs. With a single punch&#44; it can bend an iron bar right over.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (727, 'incineroar', 'fire', 'dark', 726, 375, 'After hurling ferocious punches and flinging furious kicks&#44; it finishes opponents off by spewing fire from around its navel.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (728, 'popplio', 'water', 'none', 0, 376, 'This Pok&#233;mon can control water bubbles. It practices diligently so it can learn to make big bubbles.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (729, 'brionne', 'water', 'none', 728, 376, 'It cares deeply for its companions. When its Trainer is feeling down&#44; it performs a cheery dance to try and help.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (730, 'primarina', 'water', 'fairy', 729, 376, 'Its singing voice is its chief weapon in battle. This Pok&#233;mon’s Trainer must prioritize the daily maintenance of its throat at all costs.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (731, 'pikipek', 'normal', 'flying', 0, 377, 'This Pok&#233;mon feeds on berries&#44; whose leftover seeds become the ammunition for the attacks it fires off from its mouth.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (732, 'trumbeak', 'normal', 'flying', 731, 377, 'By bending its beak&#44; it can produce a variety of calls and brand itself a noisy nuisance for its neighbors.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (733, 'toucannon', 'normal', 'flying', 732, 377, 'Within its beak&#44; its internal gas ignites&#44; explosively launching seeds with enough power to pulverize boulders.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (734, 'yungoos', 'normal', 'none', 0, 378, 'It wanders around in a never-ending search for food. At dusk&#44; it collapses from exhaustion and falls asleep on the spot.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (735, 'gumshoos', 'normal', 'none', 734, 378, 'It adores having Rattata and Raticate for dinner&#44; but as it’s diurnal&#44; it never encounters them. This Pok&#233;mon boasts incredible patience.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (736, 'grubbin', 'bug', 'none', 0, 379, 'They often gather near places frequented by electric Pok&#233;mon in order to avoid being attacked by bird Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (737, 'charjabug', 'bug', 'electric', 736, 379, 'From the food it digests&#44; it generates electricity&#44; and it stores this energy in its electric sac.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (738, 'vikavolt', 'bug', 'electric', 737, 379, 'It produces electricity via an electrical organ in its abdomen. It overwhelms bird Pok&#233;mon with shocking beams of electrical energy.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (739, 'crabrawler', 'fighting', 'none', 0, 380, 'It punches so much&#44; its pincers often come off from overuse&#44; but they grow back quickly. What little meat they contain is rich and delicious.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (740, 'crabominable', 'fighting', 'ice', 739, 380, 'It just throws punches indiscriminately. In times of desperation&#44; it can lop off its own pincers and fire them like rockets.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (741, 'oricorio-baile', 'fire', 'flying', 0, 381, 'This Oricorio has sipped red nectar. Its passionate dance moves cause its enemies to combust in both body and mind.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (742, 'cutiefly', 'bug', 'fairy', 0, 382, 'Myriads of Cutiefly flutter above the heads of people who have auras resembling those of flowers.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (743, 'ribombee', 'bug', 'fairy', 742, 382, 'Some of Ribombee’s pollen puffs are highly nutritious. They are sometimes sold as supplements.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (744, 'rockruff', 'rock', 'none', 0, 383, 'This Pok&#233;mon has lived with people since times long ago. It can sense when its Trainer is in the dumps and will stick close by its Trainer’s side.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (745, 'lycanroc-midday', 'rock', 'none', 744, 383, 'When properly raised from a young age&#44; it will become a trustworthy partner that will absolutely never betray its Trainer.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (746, 'wishiwashi-solo', 'water', 'none', 0, 384, 'It’s awfully weak and notably tasty&#44; so everyone is always out to get it. As it happens&#44; anyone trying to bully it receives a painful lesson.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (747, 'mareanie', 'poison', 'water', 0, 385, 'It’s found crawling on beaches and seafloors. The coral that grows on Corsola’s head is as good as a five-star banquet to this Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (748, 'toxapex', 'poison', 'water', 747, 385, 'Those attacked by Toxapex’s poison will suffer intense pain for three days and three nights. Post-recovery&#44; there will be some aftereffects.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (749, 'mudbray', 'ground', 'none', 0, 386, 'It has a stubborn&#44; individualistic disposition. Eating dirt&#44; making mud&#44; and playing in the mire all form part of its daily routine.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (750, 'mudsdale', 'ground', 'none', 749, 386, 'Its heavy&#44; mud-covered kicks are its best means of attack&#44; and it can reduce large trucks to scrap without breaking a sweat.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (751, 'dewpider', 'water', 'bug', 0, 387, 'When it comes across enemies or potential prey&#44; this Pok&#233;mon smashes its water-bubble-covered head into them.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (752, 'araquanid', 'water', 'bug', 751, 387, 'Despite what its appearance suggests&#44; it cares for others. If it finds vulnerable&#44; weak Pok&#233;mon&#44; it protectively brings them into its water bubble.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (753, 'fomantis', 'grass', 'none', 0, 388, 'They give off a sweet and refreshing scent. Cutiefly often gather near the tall grass where Fomantis are hiding.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (754, 'lurantis', 'grass', 'none', 753, 388, 'It fires beams from its sickle-shaped petals. These beams are powerful enough to cleave through thick metal plates.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (755, 'morelull', 'grass', 'fairy', 0, 389, 'As it drowses the day away&#44; it nourishes itself by sucking from tree roots. It wakens at the fall of night&#44; wandering off in search of a new tree.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (756, 'shiinotic', 'grass', 'fairy', 755, 389, 'It emits flickering spores that cause drowsiness. When its prey succumb to sleep&#44; this Pok&#233;mon feeds on them by sucking in their energy.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (757, 'salandit', 'poison', 'fire', 0, 390, 'Volcanoes or dry&#44; craggy places are its home. It emanates a sweet-smelling poisonous gas that attracts bug Pok&#233;mon&#44; then attacks them.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (758, 'salazzle', 'poison', 'fire', 757, 390, 'Filled with pheromones&#44; its poisonous gas can be diluted to use in the production of luscious perfumes.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (759, 'stufful', 'normal', 'fighting', 0, 391, 'A touch from anyone except a known friend sends it into a surging frenzy. It’s an incredibly dangerous Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (760, 'bewear', 'normal', 'fighting', 759, 391, 'This Pok&#233;mon has the habit of hugging its companions. Many Trainers have left this world after their spines were squashed by its hug.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (761, 'bounsweet', 'grass', 'none', 0, 392, 'Although it’s too sugary for human consumption&#44; Bounsweet’s sweat can be watered down into a juice with just the right amount of sweetness.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (762, 'steenee', 'grass', 'none', 761, 392, 'This Pok&#233;mon is always bouncing around energetically. Other Pok&#233;mon are attracted by its lively appearance and pleasant aroma.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (763, 'tsareena', 'grass', 'none', 762, 392, 'A Pok&#233;mon known for the beauty of its well-shaped legs&#44; it sometimes appears as a mascot in advertisements for beauty salons.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (764, 'comfey', 'fairy', 'none', 0, 393, 'Baths prepared with the flowers from its vine have a relaxing effect&#44; so this Pok&#233;mon is a hit with many people.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (765, 'oranguru', 'normal', 'psychic', 0, 394, 'Deep in the jungle&#44; high in the lofty canopy&#44; this Pok&#233;mon abides. On rare occasions&#44; it shows up at the beach to match wits with Slowking.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (766, 'passimian', 'fighting', 'none', 0, 395, 'They battle with hard berries for weapons. Their techniques are passed from the boss to the group&#44; generation upon generation.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (767, 'wimpod', 'bug', 'water', 0, 396, 'Its habitat varies from beaches to seabeds. A natural scavenger&#44; it will gleefully chow down on anything edible&#44; no matter how rotten.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (768, 'golisopod', 'bug', 'water', 767, 396, 'It battles skillfully with its six arms&#44; but spends most of its time peacefully meditating in caves deep beneath the sea.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (769, 'sandygast', 'ghost', 'ground', 0, 397, 'It takes control of anyone who puts a hand in its mouth. And so it adds to the accumulation of its sand-mound body.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (770, 'palossand', 'ghost', 'ground', 769, 397, 'Buried beneath the castle are masses of dried-up bones from those whose vitality it has drained.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (771, 'pyukumuku', 'water', 'none', 0, 398, 'These Pok&#233;mon line the beaches. The sticky mucous that covers their bodies can be used to soothe sunburned skin. How convenient!');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (772, 'type-null', 'normal', 'none', 0, 399, 'Due to the danger that this synthetic Pok&#233;mon may go on a rampage&#44; it wears a control mask to restrain its power.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (773, 'silvally', 'normal', 'none', 772, 399, 'Although its name was Type: Null at first&#44; the boy who evolved it into this form gave it the name by which it is now known.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (774, 'minior-red-meteor', 'rock', 'flying', 0, 400, 'Strong impacts can knock it out of its shell. This Pok&#233;mon was born from mutated nanoparticles.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (775, 'komala', 'normal', 'none', 0, 401, 'The log it holds was given to it by its parents at birth. It has also been known to cling to the arm of a friendly Trainer.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (776, 'turtonator', 'fire', 'dragon', 0, 402, 'It gushes fire and poisonous gases from its nostrils. Its dung is an explosive substance and can be put to various uses.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (777, 'togedemaru', 'electric', 'steel', 0, 403, 'The long hairs on its back act as lightning rods. The bolts of lightning it attracts are stored as energy in its electric sac.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (778, 'mimikyu-disguised', 'ghost', 'fairy', 0, 404, 'A lonely Pok&#233;mon&#44; it conceals its terrifying appearance beneath an old rag so it can get closer to people and other Pok&#233;mon.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (779, 'bruxish', 'water', 'psychic', 0, 405, 'It stuns its prey with psychokinesis and then grinds them to mush with its strong teeth. Even Shellder’s shell is no match for it.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (780, 'drampa', 'normal', 'dragon', 0, 406, 'This Pok&#233;mon is friendly to people and loves children most of all. It comes from deep in the mountains to play with children it likes in town.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (781, 'dhelmise', 'ghost', 'grass', 0, 407, 'The soul of seaweed adrift in the waves became reborn as this Pok&#233;mon. It maintains itself with new infusions of seabed detritus and seaweed.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (782, 'jangmo-o', 'dragon', 'none', 0, 408, 'They live in mountains where no trace of humans can be detected. Jangmo-o grow little by little as they battle one another.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (783, 'hakamo-o', 'dragon', 'fighting', 782, 408, 'It sheds and regrows its scales on a continuous basis. The scales become harder and sharper each time they’re regrown.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (784, 'kommo-o', 'dragon', 'fighting', 783, 408, 'Its rigid scales function as offense and defense. In the past&#44; its scales were processed and used to make weapons and other commodities.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (785, 'tapu-koko', 'electric', 'fairy', 0, 409, 'It confuses its enemies by flying too quickly for the eye to follow. It has a hair-trigger temper but forgets what made it angry an instant later.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (786, 'tapu-lele', 'psychic', 'fairy', 0, 410, 'As it flutters about&#44; it scatters its strangely glowing scales. Touching them is said to restore good health on the spot.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (787, 'tapu-bulu', 'grass', 'fairy', 0, 411, 'The guardian deity of Ula’ula is a lazy Pok&#233;mon. It commands plants to immobilize its foes and then deals them a savage blow with its horns.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (788, 'tapu-fini', 'water', 'fairy', 0, 412, 'The guardian deity of Poni&#44; it can control water. People say it can create pure water that will wash away any uncleanness.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (789, 'cosmog', 'psychic', 'none', 0, 413, 'In ages past&#44; it was called the child of the stars. It’s said to be a Pok&#233;mon from another world&#44; but no specific details are known.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (790, 'cosmoem', 'psychic', 'none', 789, 413, 'There’s something accumulating around the black core within its hard shell. People think this Pok&#233;mon may come from another world.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (791, 'solgaleo', 'psychic', 'steel', 790, 413, 'This Pok&#233;mon is said to be a male evolution of Cosmog. At the activation of its third eye&#44; it departs for another world.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (792, 'lunala', 'psychic', 'ghost', 790, 413, 'Said to live in another world&#44; this Pok&#233;mon devours light&#44; drawing the moonless dark veil of night over the brightness of day.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (793, 'nihilego', 'rock', 'poison', 0, 414, 'One of the Ultra Beasts. It’s unclear whether or not this Pok&#233;mon is sentient&#44; but sometimes it can be observed behaving like a young girl.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (794, 'buzzwole', 'bug', 'fighting', 0, 415, 'A mysterious life-form called an Ultra Beast. Witnesses saw it pulverize a dump truck with a single punch.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (795, 'pheromosa', 'bug', 'fighting', 0, 416, 'One of the Ultra Beasts. It refuses to touch anything&#44; perhaps because it senses some uncleanness in this world.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (796, 'xurkitree', 'electric', 'none', 0, 417, 'It appeared from the Ultra Wormhole. It raided a power plant&#44; so people think it energizes itself with electricity.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (797, 'celesteela', 'steel', 'flying', 0, 418, 'One kind of Ultra Beast. Witnesses have seen it burn down a forest by expelling gas from its two arms.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (798, 'kartana', 'grass', 'steel', 0, 419, 'One of the Ultra Beast life-forms&#44; it was observed cutting down a gigantic steel tower with one stroke of its blade.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (799, 'guzzlord', 'dark', 'dragon', 0, 420, 'A dangerous Ultra Beast&#44; it appears to be eating constantly&#44; but for some reason its droppings have never been found.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (800, 'necrozma', 'psychic', 'none', 0, 421, 'Light is apparently the source of its energy. It has an extraordinarily vicious disposition and is constantly firing off laser beams.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (801, 'magearna', 'steel', 'fairy', 0, 422, 'Its mechanized body is merely a vessel. Its true self is its Soul-Heart&#44; an artificial soul.');
INSERT INTO `pokemon` (`id`, `name`, `type1`, `type2`, `evolves_from_id`, `evolution_chain_id`, `description`) VALUES (802, 'marshadow', 'fighting', 'ghost', 0, 423, 'It lurks in the shadows of others&#44; copying their movements and powers. This Pok&#233;mon is craven and cowering.');
